PRAGMA foreign_keys=OFF;
BEGIN TRANSACTION;
CREATE TABLE `help_m` (
    `help_id` INTEGER NOT NULL,
    `help_category_id` INTEGER NOT NULL,
    `order_id` INTEGER NOT NULL,
    `help_item_title` TEXT,
    `help_item_title_en` TEXT,
    `help_message` TEXT,
    `help_message_en` TEXT,
    `region` INTEGER DEFAULT '0',
    `start_date` TEXT,
    `end_date` TEXT,
    PRIMARY KEY (`help_id`)
);
CREATE TABLE `help_category_m` (
    `help_category_id` INTEGER NOT NULL,
    `order_id` INTEGER NOT NULL,
    `category_name` TEXT,
    `category_name_en` TEXT,
    `billing_flag` INTEGER NOT NULL,
    `region` INTEGER DEFAULT '0',
    `start_date` TEXT,
    `end_date` TEXT,
    PRIMARY KEY (`help_category_id`)
);
COMMIT;
