INSERT INTO help_m VALUES(1,1,1,'EXP','经验',replace(replace('EXPはライブが成功するともらえるポイントです。\r\nEXPのゲージがMAXになるとRankが上がります。','\r',char(13)),'\n',char(10)),'经验是演唱会成功后所获得的点数。\n经验条达到最大之后，等级就会提升。',NULL,NULL,NULL);
INSERT INTO help_m VALUES(2,1,2,'Rank','等级',replace(replace('EXPのゲージがMAXになるとRankが上がります。\r\nRankが上がるとLPが回復してLPや友達の上限が増えます。\r\n※回復するLP量は、Rankが上がったあとの最大LP分となります。','\r',char(13)),'\n',char(10)),'蓄满经验槽就能提升等级。\n提升等级可以恢复耐力，\n同时增加耐力和好友的上限值。\n※所能恢复的耐力\n为提升等级时的最大耐力值。',NULL,NULL,NULL);
INSERT INTO help_m VALUES(3,1,3,'LP','耐力',replace(replace('ライブをプレイする時に必要なポイントです。\r\nLPが足りないとライブができないため注意してください。\r\n\r\nLPは6分経過するごとに1回復します。\r\nラブカストーンやLP回復アイテムを使うと、すぐに回復することができます。\r\n※ラブカストーンで回復するLP量は、その時のRankの最大LP分となります。','\r',char(13)),'\n',char(10)),'参加演唱会的必要点数。\n耐力不足就无法参加演唱会，还望注意。\n\n每6分钟恢复1点耐力。\n使用爱心可以立刻恢复耐力。\n※所能恢复的耐力\n为玩家等级的最大耐力值。',NULL,NULL,NULL);
INSERT INTO help_m VALUES(4,1,4,'G','金币',replace(replace('部員の練習や特別練習の時に必要です。\r\n\r\nライブの報酬や、ログインボーナスなどいろいろなタイミングでもらうことができます。','\r',char(13)),'\n',char(10)),'在进行社员练习或特训时会消耗。\n\n可通过演唱会奖励、登录奖励\n等多种方式获得。',NULL,NULL,NULL);
INSERT INTO help_m VALUES(5,1,5,'ラブカストーン','爱心',replace(replace('勧誘（特待生／限定）、LP回復、コンティニューの他に部員数の上限を増やす時などに使用することができます。\r\nラブカストーンは『ショップ』で購入することができます。\r\nまた、Gと同じように、イベントの報酬や、ログインボーナスなどいろいろなタイミングでもらうことができます。\r\n\r\nなお、ラブカストーンには有償付与分と無償付与分の区別があり、使用の際には無償付与分から消費されます。\r\n所持ラブカストーンの内訳については<a href="/webview.php/snsInfo/snsShowInfo">こちら</a>からご確認いただけます。','\r',char(13)),'\n',char(10)),'可以用于恢复耐力、演唱会失败时接关和\n招募特待生。\n\n可以在“商店”购买钻石，换取爱心\n\n和金币一样，可以在活动奖励、登录奖励等\n各种机会中获得。',NULL,NULL,NULL);
INSERT INTO help_m VALUES(6,1,6,'ラブカストーンピース','爱心碎片',replace(replace('一定数集めると「ラブカストーン」と交換される特別なアイテムです。\r\n\r\nスペシャルごほうびBOXから一定確率で出現し、一定数集まると自動的にラブカストーンへの交換が行われます。\r\n\r\n交換に必要なラブカストーンピースの数は、交換を重ねるごとに増加し、毎月1日にリセットされます。\r\n\r\n※所持しているラブカストーンピースの数はリセットされません。\r\n※「課題」の「その他」カテゴリーから交換に必要な数や現在所持しているラブカストーンピースの数を確認することができます。','\r',char(13)),'\n',char(10)),'收集一定数量就能兑换成“爱心”的特别道具。\n在特别奖励盒里有一定的出现机率，收集一定数量时会自动兑换成爱心。\n每自动兑换一次爱心后，下一次兑换爱心所需的碎片数就会增加；兑换爱心的所需碎片数会在每个月1号回复成预设值。\n※持有的爱心碎片不会跟着回复成初始预设值\n※可以从“课题”里的“其他”项目中确认下一次兑换成爱心所需的碎片数以及目前持有的碎片数',NULL,NULL,NULL);
INSERT INTO help_m VALUES(7,1,7,'ナビゲーター','引导员',replace(replace('『ホーム』の左であなたをナビゲートしてくれる部員です。\r\n\r\n部員一覧でナビゲーターに設定したい部員をタップし、「パートナーにする」を選択すると登場します。\r\nほかの部員もきてくれることがあります。','\r',char(13)),'\n',char(10)),'在首页左边负责引导你的社员\n\n一般为主要角色。\n偶尔也会有其他社员来负责引导。',NULL,NULL,NULL);
INSERT INTO help_m VALUES(8,1,8,'ログインボーナス','登录奖励',replace(replace('アプリを起動すると、1日1回ログインボーナスがもらえます。\r\nその日に指定されたボーナスを獲得できます。\r\n\r\n※ログインボーナスは毎日AM0:00に更新されます。','\r',char(13)),'\n',char(10)),'每天登录游戏就可以获得登录奖励。\n\n如果一个月内，连续每天登录\n就可以获得与以往不同的豪华奖励。\n\n登录奖励在每天的凌晨0:00更新。',NULL,NULL,NULL);
INSERT INTO help_m VALUES(9,1,9,'プレゼントボックス','礼物盒',replace(replace('プレゼントをもらったりするとこちらに届きます。\r\nプレゼントには受取期限があるため注意してください。\r\n\r\n【交換チケットについて】\r\n交換チケット1枚につき、指定のラインナップのいずれか1つと交換することができます。\r\nラインナップは交換チケットの種類により異なります。\r\n交換は、プレゼントボックスから交換チケットを受け取る際に行われます。\r\n一度交換すると対象を選択し直すことはできません。\r\n交換チケットの交換期限はありません。','\r',char(13)),'\n',char(10)),replace(replace('收到的礼物将被送到这里。\r\n请注意礼物存在领取时限。\r\n\r\n【关于兑换券】\r\n1张兑换券可以兑换指定阵容中的任一对象。\r\n阵容视兑换券的种类而异。\r\n兑换将在从礼物箱中领取兑换券时进行。\r\n兑换后将无法重新选择对象。\r\n兑换券没有兑换时限。','\r',char(13)),'\n',char(10)),NULL,NULL,NULL);
INSERT INTO help_m VALUES(10,1,10,'おまかせ転部','系统筛选退社',replace(replace('おまかせ転部では、プレゼントボックス内の特定条件のレアリティNの部員をまとめて転部することができます。\r\n条件は以下の通りです。\r\n\r\n・サイドストーリー開放済み\r\n・サポートメンバーではない','\r',char(13)),'\n',char(10)),'使用“系统筛选退社”功能，\n可以让礼物盒里符合特定条件的\nN社员全部一起退社。\n筛选的特定条件如下：\n・支线剧情已解锁完毕\n・非支援成员',NULL,NULL,NULL);
INSERT INTO help_m VALUES(11,1,11,'受取履歴','领取记录',replace(replace('今まで受取ったプレゼントの履歴を確認できます。\r\n\r\n※確認できる期間は受取りから2週間までとなります。','\r',char(13)),'\n',char(10)),'可以查看至今为止领取礼物的记录。\n\n※领取礼物的记录最多会保持2周的时间。',NULL,NULL,NULL);
INSERT INTO help_m VALUES(12,1,12,'一括受取','全部领取',replace(replace('プレゼントをまとめて受取ることができます。\r\n表示順に上から1000件までまとめて受取れます。\r\n一括で受取れなかった時はプレゼントボックスに残ります。\r\n','\r',char(13)),'\n',char(10)),'可以一并领取所有的礼物。\n如果未能全部领取，未能领取的礼物会继续留在礼物盒中',NULL,NULL,NULL);
INSERT INTO help_m VALUES(13,1,13,'未受取一覧','未领取一览',replace(replace('まだ受取っていないプレゼントを確認することができます。\r\n部員数の上限を超えた時にやってきた部員もこちらにいます。','\r',char(13)),'\n',char(10)),'可以确认尚未领取的礼物。\n当社员数量超过上限时，新加入的社员也会出现在这里。',NULL,NULL,NULL);
INSERT INTO help_m VALUES(14,1,18,'新着リスト','新消息','新しい情報を見ることができます。','可以查看新到的消息。',NULL,NULL,NULL);
INSERT INTO help_m VALUES(15,1,19,'友達の動き','好友动态','友達の新しい情報を確認できます。','可以查看好友的近况。',NULL,NULL,NULL);
INSERT INTO help_m VALUES(16,1,20,'メールBOX','邮件一览','ここにあなた宛てのメールが届きます。','可以查看寄给你的邮件。',NULL,NULL,NULL);
INSERT INTO help_m VALUES(17,1,21,'送信履歴','发送记录','あなたが送信したメールを見ることができます。','可以查看自己寄出的信件。',NULL,NULL,NULL);
INSERT INTO help_m VALUES(18,1,14,'友達リスト','好友','ライブの時にゲストとしてあなたをサポートしてくれます。','在演唱会时，好友会作为嘉宾来帮助你。',NULL,NULL,NULL);
INSERT INTO help_m VALUES(19,1,15,'友達一覧','好友一览',replace(replace('友達になった学院生のプロフィールをチェックしたりメールを送ったりすることができます。\r\n友達リストの整理もここでできます。\r\n\r\n友達リストの右上にあるプルダウンメニューから、並べ替えて見ることもできます。','\r',char(13)),'\n',char(10)),'可查看好友的个人资料\n或向好友发送邮件。\n或者从好友一览将其删除。\n\n可以通过好友一览右上角的筛选功能\n对好友进行排序。',NULL,NULL,NULL);
INSERT INTO help_m VALUES(20,1,16,'申請中','申请一览','あなたが友達申請をしている学院生を見ることができます。','可以查看到你所发送的好友申请。',NULL,NULL,NULL);
INSERT INTO help_m VALUES(21,1,17,'承認待ち','待确认一览','あなたに友達申請をしてくれている学院生を見ることができます。','可以查看到他人向你发送的好友申请。',NULL,NULL,NULL);
INSERT INTO help_m VALUES(22,1,22,'ID検索','搜索账号',replace(replace('IDを入力して友達を見つけることができます。\r\nあなたのIDもここで確認することができます。','\r',char(13)),'\n',char(10)),'可以通过账号来搜索好友。\n也可以在这里确认自己的账号。',NULL,NULL,NULL);
INSERT INTO help_m VALUES(23,1,23,'課題','课题',replace(replace('ホームの右中央にある課題ボタンから利用できます。\r\n\r\n課題を達成することでストーリーやライブの楽曲が解禁されたり、部員数の上限が増えるなどの報酬が獲得できます。','\r',char(13)),'\n',char(10)),'可以点击主界面中间右侧的课题按键使用。\n\n完成课题就可以解锁\n新的故事和演唱会歌曲，\n也能获得提升社员上限\n等奖励。',NULL,NULL,NULL);
INSERT INTO help_m VALUES(25,1,25,'お知らせ','公告','アップデート情報やメンテナンス情報などの事務局からのお知らせを確認できます。','可以确认更新信息维护信息等\n运营团队发布的消息。',NULL,NULL,NULL);
INSERT INTO help_m VALUES(26,1,26,'うでまえクラス','演唱会段位挑战',replace(replace('うでまえクラスは、SCOREは影響せず、COMBOやタップ評価などの結果をもとに自分のうでまえを評価するシステムです。\r\n\r\nそれぞれのクラスに応じて試験が用意されており、クラスによっていくつかの試験をクリアすれば昇格できるもの全ての試験をクリアしなければ昇格できないものと様々な条件があります。\r\n\r\n現在、うでまえクラスでは「ロイヤルエキスパート」まで上がることができます。\r\nそれより先のクラスについては、今後のアップデートで追加する予定です。','\r',char(13)),'\n',char(10)),'当玩家等级达到Rank30就可以进行“演唱会段位挑战”。\n“演唱会段位挑战”是一个以连击数和点击评价作为评价基准、\n不受得分高低影响的演唱会实力评价系统。\n\n各段位都设有专属于该段位的测验。\n根据不同段位，会有只要通过几项测验就能升级的条件设定，\n也有一定要通过全部测验才能升级的条件设定。\n\n现阶段“演唱会段位挑战”的最高层级是“皇家级高手”。\n不过在未来的版本升级中，也预计会再增设更高的层级。',NULL,NULL,NULL);
INSERT INTO help_m VALUES(27,1,27,'うでまえクラスの通常試験','演唱会实力层级的普通测验',replace(replace('うでまえクラスの通常試験はライブプレイ時の結果が条件を満たしている場合に達成することができます。\r\n\r\n通常試験は以下のライブプレイ時に有効となります。\r\n\r\n・通常楽曲\r\n・特別楽曲\r\n・ランダムセレクト\r\n・MASTER楽曲プレイ機能\r\n・スコアマッチ\r\n・メドレーフェスティバル\r\n・チャレンジフェスティバル\r\n・なかよしマッチ\r\n・おさんぽラリー','\r',char(13)),'\n',char(10)),'演唱会实力层级的普通测验的通过条件\n可以在游玩演唱会时达成。\n\n游玩以下演唱会时都会列入普通测验的条件统计范围。\n\n・普通歌曲\n・特别歌曲\n・随机选曲\n・MASTER曲目练习功能\n・SCORE MATCH\n・MEDLEY FESTIVAL\n・CHALLENGE FESTIVAL\n・友情大合战\n・漫步关卡战',NULL,NULL,NULL);
INSERT INTO help_m VALUES(28,1,28,'うでまえクラスの認定試験','演唱会实力层级的资格测验',replace(replace('うでまえクラスの認定試験はホーム＞うでまえクラス画面から挑戦することができます。\r\n\r\n通常試験と違い、指定の挑戦回数で全ての試験をクリアしなければいけません。\r\n\r\n認定試験には次の2種類があります。\r\n\r\n・1曲ごとに条件を満たしてクリアしていくもの\r\n・連続で楽曲をプレイし、最終的に条件を満たす必要があるもの\r\n\r\n認定試験に挑戦する際のユニットは全て試験ごとに設定されたユニットを使用することになり、自分のユニットを使うことができません。','\r',char(13)),'\n',char(10)),'点击游戏首页的图示就能进到演唱会实力层级专页\n去挑战演唱会实力层级的资格测验。\n\n跟普通测验不同的是，资格测验必须要在指定的挑战次数内\n通过全部的测验才行。\n\n资格测验分为以下两大类。\n\n・仅须达成游玩1首曲目的指定条件即可通过的测验\n・必须连续游玩复数曲目，并达成指定条件才可通过的测验\n\n挑战资格测验时无法使用自己编组的队伍，\n而须使用系统在每项测验自动设定好的队伍。',NULL,NULL,NULL);
INSERT INTO help_m VALUES(29,1,29,'認定試験の挑戦回数','资格测验的可挑战次数',replace(replace('認定試験の挑戦回数は一定期間ごとにリセットされ、再度挑戦を行えるようになります。\r\n挑戦回数のリセット期間については以下の3種類あります。\r\n\r\n・毎日0:00にリセット\r\n・毎週月曜0:00にリセット\r\n・毎月1日0:00にリセット\r\n\r\n※リセットタイミングで、途中だった試験の内容もリセットされます。\r\n※クリア済みの課題はリセットされません。','\r',char(13)),'\n',char(10)),'资格测验的可挑战次数会在固定的时间点回复为预设值，\n所以可以不断重新挑战。\n可挑战次数的回复时间点分为以下3种。\n\n・每天上午8:00\n・每周一上午8:00\n・每个月1号上午8:00\n\n※当可挑战次数回复为预设值时，\n挑战到一半的内容也会还原到未挑战前的预设状态。\n※已达成的条件不会被还原为预设值。',NULL,NULL,NULL);
INSERT INTO help_m VALUES(30,1,30,'うでまえクラス称号について','演唱会实力层级称号',replace(replace('自分のうでまえクラスを表す称号は、常に自分のいるクラスの称号が表示されます。\r\nうでまえクラスの称号は称号画面より設定することが可能です。\r\n\r\n※自分が過去に獲得したクラスの称号は表示されません。','\r',char(13)),'\n',char(10)),'在演唱会实力层级页面显示的，\n会是自己平常大多时候的层级状态。\n演唱会实力层级称号也可以设定为首页显示的称号。\n\n※称号清单中不会显示自己过去获得的称号',NULL,NULL,NULL);
INSERT INTO help_m VALUES(31,2,1,'メインストーリー','主线剧情',replace(replace('部員たちとのストーリーを見ることができます。\r\nストーリーの解放条件には以下のようなものがあります。\r\n\r\n・μ''sのメインストーリーRankを上げたり、特定の楽曲をクリアする\r\n\r\n・AqoursのメインストーリーAqours課題をたくさん達成したり、特定の楽曲をクリアする\r\n\r\nまた、新しいストーリーを読むと新しい楽曲が追加されたり、新たな課題が設定されたりします。','\r',char(13)),'\n',char(10)),'可以观看社员们的故事。\n完成后可以解锁新的乐曲、\n追加新的课题。',NULL,NULL,NULL);
INSERT INTO help_m VALUES(32,2,2,'サイドストーリー','支线剧情',replace(replace('部員たちとのサイドストーリーを見ることができます。\r\n覚醒した部員との絆がMAXになると解禁されます。\r\n\r\n絆はライブで深めることができます。\r\n\r\n覚醒や絆について詳しく知りたい場合は『ヘルプ』の『部員』に記載されています。','\r',char(13)),'\n',char(10)),'可以查看与社员之间发生的支线剧情。\n与觉醒社员之间的亲密度达到最大时将会解锁支线剧情。\n\n通过演唱会可以加深与社员间的亲密度！\n\n觉醒以及亲密度的详细信息\n可以在“说明”中的“社员”部分查看。',NULL,NULL,NULL);
INSERT INTO help_m VALUES(33,2,3,'その他のストーリー','其他剧情',replace(replace('「その他のストーリー」では以下のストーリーを見ることができます。\r\n\r\n・解放済みのイベントストーリー\r\n・位置情報を使用することで獲得できるストーリー\r\n・課題を達成することで獲得できるストーリー\r\n\r\n※イベント期間中に解放しなかったイベントストーリーは、イベント終了してから一定期間後にイベントストーリー解放キーで解放することができます。','\r',char(13)),'\n',char(10)),'在"其他剧情"中可以观赏到以下故事剧情：\n・之前已成功解锁的活动剧情\n・可透过定位功能获得的剧情\n・可透过完成特定课题获得的剧情',NULL,NULL,NULL);
INSERT INTO help_m VALUES(34,2,4,'イベントストーリー解放キー','活动剧情解锁钥匙',replace(replace('未獲得のイベントストーリーを解放できるアイテムです。\r\n\r\nイベントストーリー解放キーを1個使用することでストーリーを1話解放することができます。\r\n解放可能なストーリーは、左側に解放ボタンが表示されます。\r\n\r\n※解放可能なイベントストーリーは、月末のアップデートにて1か月前に配信したストーリーを対象に追加しています。','\r',char(13)),'\n',char(10)),'活动剧情解锁钥匙是可以用来解锁过去没解锁过的\n活动剧情的道具。\n\n使用1把活动剧情解锁钥匙可以解锁1话剧情。\n可解锁的剧情会在左边显示一个解锁按钮。\n\n※活动剧情解锁钥匙仅能用来解锁活动剧情',NULL,NULL,NULL);
INSERT INTO help_m VALUES(35,2,5,'MENUについて','关于菜单','ストーリー再生中、右上にあるMENUボタンを押すと早送り、スキップ、オート、ログ表示やウィンドウの非表示などの機能一覧が表示されます。','在剧情右上方的菜单中\n可以选择快进，自动播放，显示会话记录\n或取消文字显示。',NULL,NULL,NULL);
INSERT INTO help_m VALUES(36,2,6,'早送り','快进',replace(replace('ストーリーを早送りすることができます。\r\n止めたい時は画面をもう一度タップしてください。','\r',char(13)),'\n',char(10)),'可以快进剧情。\n想要停止的话记得再点一次哦。',NULL,NULL,NULL);
INSERT INTO help_m VALUES(37,2,7,'スキップ','跳过',replace(replace('ストーリーの再生をスキップすることができます。\r\nスキップすると、ストーリークリアとなります。','\r',char(13)),'\n',char(10)),'可以跳过剧情。\n若选择跳过剧情的话，将会立刻完成剧情。',NULL,NULL,NULL);
INSERT INTO help_m VALUES(38,2,8,'ログ表示','显示会话记录',replace(replace('既に読んだテキストを再度表示できます。\r\n聞きそびれたセリフをもう一度聞くことができます。','\r',char(13)),'\n',char(10)),'可以将大家的会话都显示出来。\n错过的对话也都可以在这里再次播放。',NULL,NULL,NULL);
INSERT INTO help_m VALUES(39,2,9,'オート','自动播放',replace(replace('自動でストーリーを進めてくれる機能です。\r\n止めたい時はMENUボタンをタップしてください。','\r',char(13)),'\n',char(10)),'即使不点击画面会话也可以自动播放，\n想停止的话就再点击一次菜单，即可停止自动播放模式。',NULL,NULL,NULL);
INSERT INTO help_m VALUES(40,2,10,'ウィンドウ非表示','取消对话框显示',replace(replace('メッセージの部分を非表示にすることができます。\r\nもう一度タップするとメッセージが表示されます。','\r',char(13)),'\n',char(10)),'可以取消文字部分的显示。\n再点一次的话文字就会重新出现。',NULL,NULL,NULL);
INSERT INTO help_m VALUES(41,2,11,'μ''s・Aqours切り替え','μ''s/Aqours切换',replace(replace('μ''sとAqoursのストーリーメニューを切り替えます。\r\n切り替えると、ライブや勧誘などにも反映されます。','\r',char(13)),'\n',char(10)),'可以切换μ''s和Aqours的剧情菜单。\n切换之后，演唱会和社员招募等方面都会发生相应变化。',NULL,NULL,NULL);
INSERT INTO help_m VALUES(42,3,1,'部員について','关于社员',replace(replace('部活を一緒に頑張っている仲間のことです。\r\n学校を盛り上げるため、ライブをして転入生を集めています。','\r',char(13)),'\n',char(10)),'一起参加偶像活动的社员们。\n为了挽救学校的未来，请通过演唱会来召集新社员吧。',NULL,NULL,NULL);
INSERT INTO help_m VALUES(43,3,2,'経験値','经验值',replace(replace('部員が練習をするとたまります。\r\n経験値がたまると、部員のレベルが上がります。','\r',char(13)),'\n',char(10)),'社员进行练习就可以获得经验值。\n若经验值累计到一定数值，社员的等级将会提升。',NULL,NULL,NULL);
INSERT INTO help_m VALUES(44,3,3,'レベル','社员等级','レベルが上がると部員の能力が上がります。','等级提升的话，社员的能力就会提高。',NULL,NULL,NULL);
INSERT INTO help_m VALUES(45,3,4,'絆','绊',replace(replace('ライブをすると部員との絆が深まります。\r\n絆が深まると部員の能力が上がり、覚醒した部員との絆がMAXになるとその部員のサイドストーリーを読むことができます。\r\n\r\nライブでたくさんのCOMBOをつなげると部員との絆がさらに深まります。\r\n\r\nセンターポジションの部員との絆は他の部員よりも深まりやすくなっています。','\r',char(13)),'\n',char(10)),'举办演唱会能加深社员之间的绊。\n随着绊的加深社员的能力也会提高。\n如果和觉醒后的社员的绊达到最大\n就可以触发该社员的支线剧情哦。\n\n在演唱会中尽可能的完成更多的连击\n是加深社员绊的诀窍。\n\n相比其它社员，主唱位置社员的\n绊将会更快地提升！',NULL,NULL,NULL);
INSERT INTO help_m VALUES(46,3,5,'属性','属性',replace(replace('部員の能力には、スマイル、ピュア、クール、全属性の4種類があります。\r\nピンクがスマイル、グリーンがピュア、ブルーがクール、パープルが全属性です。','\r',char(13)),'\n',char(10)),'社员的能力分为甜美，清纯，洒脱和全属性\n总共4种属性。\n粉红色代表甜美，绿色代表清纯，蓝色代表洒脱\n紫色代表全属性。',NULL,NULL,NULL);
INSERT INTO help_m VALUES(47,3,6,'特技','技能',replace(replace('特技を持っている部員はライブで大活躍します。\r\n特技を披露してライブが成功しやすくなります。\r\n\r\n特技にはLvがあり、同じ特技を持つ部員と練習をすると特技用の経験値を獲得します。\r\n経験値がたまると特技Lvがアップします。','\r',char(13)),'\n',char(10)),'拥有技能的社员可以在演唱会中触发技能\n技能可以使演唱会更容易成功。\n\n技能分为不同的等级，\n与拥有相同技能的社员一起练习\n即可获得技能经验值。\n积累经验的话就可以提升技能等级。',NULL,NULL,NULL);
INSERT INTO help_m VALUES(48,3,7,'特技の種類','技能的种类',replace(replace('・SCOREアップ\r\n効果量分のSCOREが加算されます。\r\n\r\n・体力回復\r\n効果量分の体力が回復します。\r\nまた、体力がMAXを超えて回復すると、ゲージを貯めパラメータをアップさせることができます。\r\n\r\n・判定強化\r\nリズムアイコンのタップ評価をサポートします。\r\n発動中に特技の効果によらず自身のうでまえでPERFECTを取ると、タップしたときのSCOREにボーナスがかかります。\r\n\r\n判定強化には以下の2種類があります。\r\n\r\n＜判定強化・大＞\r\n一定時間、リズムアイコンのタップ評価GOOD以上をPERFECTにします。\r\n\r\n＜判定強化・小＞\r\n一定時間、リズムアイコンのタップ評価GREATをPERFECTにします。\r\n\r\n\r\n・特技発動率アップ\r\n一定時間、特技発動率アップを除いた他の特技が発動しやすくなります。\r\n\r\n・PERFECT SCOREアップ\r\n一定時間、PERFECT時のタップでの獲得SCOREをアップします。\r\n\r\n・パラメータアップ\r\n一定時間、特技効果で指定した部員の属性Pをアップします。\r\n\r\n・特技リピート\r\n直前に発動した、特技リピート以外の特技の効果を発動します。\r\n\r\n・パラメータシンクロ\r\n一定時間、特定の部員と同じ属性Pになることができます。\r\n\r\n・特技レベルブースト\r\n次に発動する特技は指定された分だけLvがアップした状態で効果を発揮します。\r\n\r\n・COMBO FEVER\r\n一定時間、達成しているコンボ数に応じてタップSCOREが増えます。','\r',char(13)),'\n',char(10)),'・得分提升\n提升效果数量的得分。\n\n・体力回复\n回复效果数量的体力。\n\n・判定强化・大\n在一定时间内，将GOOD以上的节奏图标点击评价提升为PERFECT。\n\n・判定强化・小\n在一定时间内，将GREAT以上的节奏图标点击评价提升为PERFECT。\n\n・特技发动率提升\n在一定时间内，除“特技发动率提升”以外的其它特技更容易发动。\n\n・PERFECT 得分提升\n在一定时间内，提升PERFECT时点击可获得的得分量。\n\n・属性提升\n在一定时间内，通过特技效果提升制定社员的属性。\n\n・特技重复\n再次发动上一个已发动过且非“特技重复”的特技。\n\n・特技同步\n在一定时间内，将属性转变为与特定社员相同。\n\n・特技等级加成\n下次发动的特技以提升指定等级后的状态发挥效果。\n\n・COMBO FEVER\n在一定时间内，根据达成的连击数来提升点击获得的得分。',NULL,NULL,NULL);
INSERT INTO help_m VALUES(49,3,8,'特技発動条件','技能发动条件',replace(replace('特技には発動条件があり、これを満たした時指定の確率で特技を発動します。\r\n発動条件には以下のようなものがあります。\r\n\r\n・一定数のリズムアイコンの登場\r\n・一定のコンボ数の達成\r\n・一定のPERFECT数の達成\r\n・一定時間の経過\r\n・一定のスコア獲得\r\n・スターアイコンPERFECTの達成\r\n・特定のメンバーの特技発動','\r',char(13)),'\n',char(10)),'特技存在发动条件，满足此条件后，特技便会以指定概率发动。\n发动条件如下所示。\n\n・一定数量的节奏图标登场\n・达成一定的连击数\n・达成一定的PERFECT数\n・经过一定时间\n・获得一定的分数\n・达成星星图标PERFECT\n・特定成员的特技发动',NULL,NULL,NULL);
INSERT INTO help_m VALUES(50,3,9,'センタースキル','主唱技能',replace(replace('特別な特技を持っている部員はセンターポジションにいると、その能力を発揮させてライブが成功しやすくなります。\r\n\r\nSSRやURの部員のセンタースキルには、特定条件の部員に対してさらに能力を発揮させる効果があります。','\r',char(13)),'\n',char(10)),'将拥有特殊技能的社员\n设为主唱，即可发会此技能的作用\n让演唱会更容易获得成功。\n\nSSR和UR的社员的主唱技能\n可对特定条件的社员\n发挥更加强力的效果。',NULL,NULL,NULL);
INSERT INTO help_m VALUES(51,3,10,'レアリティ','稀有度',replace(replace('部員のレアリティは全部で5種類あります。\r\n\r\n・N（ノーマル）\r\n・R（レア）\r\n・SR（スーパーレア）\r\n・SSR（ダブルスーパーレア）\r\n・UR（ウルトラレア）\r\n\r\n下にいくほどレアリティが高くなります。','\r',char(13)),'\n',char(10)),'社员的稀有度一共有5种\n\n・N（Normal ）\n・R（Rare）\n・SR（Super Rare）\n・SSR(Double Super Rare)\n・UR（Ultra Rare）\n\n越接近UR稀有度就越高.',NULL,NULL,NULL);
INSERT INTO help_m VALUES(52,3,11,'Type','类型',replace(replace('Typeは部員の種類を分類するものです。\r\nμ''sとAqoursと虹ヶ咲学園スクールアイドル同好会、学年と所属ユニットが設定されています。\r\n\r\n部員リストやアルバムではTypeを指定して、表示する部員を絞り込むことができます。','\r',char(13)),'\n',char(10)),'类型是对社员进行的分类。\nμ''s和Aqours的各个社员在设定上分别属于不同的年级和队伍。\n在社员列表和相册中可以选择社员的类型，\n然后筛选特定类型的社员。\n',NULL,NULL,NULL);
INSERT INTO help_m VALUES(53,3,12,'サポートメンバー','支援成员',replace(replace('部員の中にはユニットに参加してのライブや特別練習（覚醒）ができないサポートメンバーもいます。\r\n\r\nイベントの報酬、補助チケット勧誘から仲間になってくれます。\r\n練習や転部をすることで力を発揮するメンバーや特技アップをサポートしてくれるメンバーがいます。\r\n\r\n※サポートメンバーは転部・練習によるシール獲得の対象外となります。','\r',char(13)),'\n',char(10)),'在所有社员中，存在着编入队伍后\n也无法进行演唱会特别练习（觉醒）的支援社员\n这种社员可通过活动奖励，\n或辅助券招募获得。\n\n这些社员会在列表中被显示为“支援社员”，\n且不会被计入社员人数当中。\n\n·羊驼（白）\n 劝退或练习时\n能获得更多的G和经验值。\n\n老师\n在练习时，能够获得\n比羊驼更多的经验值。\n劝退后便无法发挥此项能力，要注意哦。\n\n・技能提升支援社员\n与相同属性社员一起练习的话\n即可获得技能经验值。\n技能提升支援社员的稀有度越高，\n可获得的技能经验值就越多。\n积累经验值的话就可以提升技能等级。\n',NULL,NULL,NULL);
INSERT INTO help_m VALUES(54,3,14,'ユニット編成','更改队伍',replace(replace('ライブに出演する部員を選んだり、入れ替えたりすることができます。\r\nライブ時のメンバー配置の変更もできます。','\r',char(13)),'\n',char(10)),'可选择或替换\n参加演唱会的社员。\n也可更改参加演唱会的社员配置。',NULL,NULL,NULL);
INSERT INTO help_m VALUES(55,3,15,'ユニット数','队伍数',replace(replace('ユニットは全部で18個作ることができます。\r\n楽曲に合わせていろんなユニットを作って、ライブを盛り上げましょう。','\r',char(13)),'\n',char(10)),'最多可拥有7支队伍\n分为A,B,C,D,E,F,G,H,I共9队。\n可根据歌曲的不同编辑不同的队伍\n让演唱会更容易成功。',NULL,NULL,NULL);
INSERT INTO help_m VALUES(56,3,16,'ユニット名変更','更改队伍名',replace(replace('ユニット名を変えることができます。\r\n好きなユニット名を入力してください。','\r',char(13)),'\n',char(10)),'可以更改队伍名称。\n请选择你喜欢的名称吧。',NULL,NULL,NULL);
INSERT INTO help_m VALUES(57,3,17,'自動編成','自动组队',replace(replace('部員の属性や特技、Typeの条件を指定して、オススメのユニットを組むことができます。\r\n\r\n・センターポジションを固定選択したセンターの部員を動かしたくない時や、センターの部員をあとから選びたい時に使います。\r\n\r\n・絆ptがMAXになっていない覚醒部員を優先絆ptを貯めたい覚醒済み部員がいる時に使います。\r\n\r\n・補正なしのステータスで編成するセンタースキルやSIスキル、絆ptによるステータス補正を含まずに選びたい時に使います。','\r',char(13)),'\n',char(10)),'选择社员的属性、技能、类型等筛选条件，\n组建由系统推荐的队伍。\n\n・在不想更改固定\n在主场位置上的主场社员时，\n或是想要治好再选择主场社员时使用\n\n・希望让特定的亲密度未达到最大的觉醒社员\n优先积累亲密度时使用\n',NULL,NULL,NULL);
INSERT INTO help_m VALUES(58,3,18,'練習','练习',replace(replace('部員同士で練習をすると部員の経験値が上がります。\r\n以下の表示が出た場合は、経験値がさらにアップします。\r\n・SUPER SUCCESS：経験値1.5倍\r\n・ULTRA SUCCESS：経験値2倍\r\n※特技経験値は対象外となります。\r\n\r\n練習をすると、練習に付き合ってくれた部員はいなくなるので気を付けて選んでください。\r\n一部を除くR以上のレアリティの部員を練習相手にした時はシールSHOPで利用できるシールを獲得できます。\r\n\r\n※一度、練習した部員を戻すことはできません。練習をさせたくない部員については、部員リストから保護していただくことで、誤操作が防げます。','\r',char(13)),'\n',char(10)),'社员之间进行练习的话可以增加社员的经验值。\n有机会获得大量的经验值。\n\n但如果进行练习的话，陪练的社员\n将会消失，所以要谨慎挑选哦。\n除了部分社员之外，稀有度R或以上的社员进行陪练时\n可获得能够在贴纸商店中使用的贴纸。\n\n*消失后的社员将不会再次出现。\n如果有不想设为陪练的社员\n请在"社员一览"中将其锁定，\n以防止误操作的发生。',NULL,NULL,NULL);
INSERT INTO help_m VALUES(59,3,19,'特別練習','特训',replace(replace('部員を覚醒させる特別な練習のことです。\r\nまた、SIスキルをセットするためのスロットも解放されます。\r\n\r\n・部員から選ぶ\r\n同じ名前の部員でもアルバム詳細の部員Noが同じでないと特別練習ができません。特別練習すると、特別練習に付き合ってくれた部員はいなくなってしまうので、気を付けて選んでください。覚醒した時にスロットが1増加します。\r\n\r\n・シールから選ぶ\r\n「シール」を使って特別練習を行います。必要なシールの枚数は部員のレアリティによって異なります。シールで覚醒した時は、スロットは増えません。\r\n\r\n覚醒済みの部員でも、SIスキルのスロットを増やすことができる場合はさらに特別練習を行うことができます。\r\n同じ部員同士の場合は2、シールを使った場合は1増加します。','\r',char(13)),'\n',char(10)),'可以让社员觉醒的特训。\n此外，也可以解锁放置学园偶像技能的技能槽。\n\n・即使从社员中选出了相同名字的社员\n如果相册详情中的社员编号不同的话\n也无法进行特训。\n进行特训后，陪练的社员\n将会消失，要谨慎选择哦。\n觉醒后，社员的技能槽就会增加1个。\n\n・从“贴纸”功能中选择\n贴纸并使用，就能进行特训。\n根据社员稀有度的不同，需要的贴纸数量也会有所差别。\n通过贴纸觉醒后，技能槽不会增加。\n\n即使是已经觉醒的社员，\n如果仍然能够增加学园偶像技能槽的话，\n那么就依然可以进行特训。\n相同社员进行特训可增加2个技能槽，使用贴纸进行特训可增加1个技能槽。',NULL,NULL,NULL);
INSERT INTO help_m VALUES(60,3,20,'覚醒','觉醒',replace(replace('部員が覚醒をすると、見た目が変わってレベルと絆の上限値もアップします。\r\n\r\nさらに、覚醒した部員との絆がMAXになるとその部員のサイドストーリーを読めるようになります。\r\n※サイドストーリーは後日追加になる場合もございます。','\r',char(13)),'\n',char(10)),'当社员觉醒后外观发生变化\n等级及亲密度的上限也会提升\n\n觉醒后的社员亲密度为最大时\n该社员的支线剧情就会被解锁。',NULL,NULL,NULL);
INSERT INTO help_m VALUES(61,3,24,'転部','退社',replace(replace('部員が転部する時、Gをもらえますが部員はいなくなってしまうので注意してください。\r\n一部を除くR以上のレアリティの部員を転部させた時はシールSHOPで利用できるシールを獲得できます。\r\n\r\n※一度、転部した部員を戻すことはできません。転部などをさせたくない部員については、部員リストで保護していただくことで、誤操作が防げます。\r\n\r\nまた、画面上部の「おまかせ」ボタンをタップすると、以下の特定条件を除いたN部員をまとめて選択することができます。\r\n\r\n・サイドストーリー未解禁\r\n・保護、パートナーに設定中\r\n・絆MAX\r\n・メインユニットに編成中','\r',char(13)),'\n',char(10)),'让社员退社可以获得金币，<br />但退社的社员将会消失不见，还望注意。<br />除部分社员外，让稀有度R以上的社员退部，<br />就可以获得能在贴纸商店使用的贴纸。<br /><br />※选择退社的社员将无法恢复。<br /> 可以从社员列表中对<br /> 不想让其退社的社员进行保护，<br /> 避免操作失误。',NULL,NULL,NULL);
INSERT INTO help_m VALUES(62,3,25,'部員リスト','社员一览',replace(replace('アイドル部に所属している部員を確認できます。\r\n部員のアイコンをタップすると詳しい情報が確認できます。\r\n部員リストの右上にあるプルダウンメニューから、並び替えて見ることもできます。','\r',char(13)),'\n',char(10)),'可以查看所持社员。\n长按社员还可以看到该社员的详细资料。\n点击社员一览右上方的按钮\n可对社员进行排序。',NULL,NULL,NULL);
INSERT INTO help_m VALUES(63,3,26,'お気に入り登録','加入保护',replace(replace('部員の保護をすると練習及び特別練習の相手をすることや、転部ができなくなります。\r\n\r\nなお、保護は解除することもできます。','\r',char(13)),'\n',char(10)),'被保护的社员<br />将无法参加练习、特殊练习和退部。<br /><br />可以解除保护。',NULL,NULL,NULL);
INSERT INTO help_m VALUES(64,3,27,'部員数','社员数',replace(replace('部員数が上限を超えると、それ以上部員を増やせなくなります。\r\n\r\n『ショップ』でラブカストーンを1個消費して、部員数を4人増やすことができます。\r\n最大で2200人まで増やすことができます。','\r',char(13)),'\n',char(10)),'社员数一旦超过上限值，<br />将无法继续增加新社员。<br /><br />可以在“商店”使用爱心增加上限值。<br /><br />※使用爱心最多可以增加社员上限值<br /> 220次。',NULL,NULL,NULL);
INSERT INTO help_m VALUES(65,3,28,'控え室','学园偶像技能',replace(replace('部室にいるメンバーを控え室に移動することができます。\r\n\r\n※控え室に移動できる部員は700人までです。\r\n※控え室のメンバーは「ユニット編成」「練習」「特別練習」等ができません。','\r',char(13)),'\n',char(10)),'设置后就能够进一步强化社员能力的特别技能。\n学园偶像技能\n可通过举行演唱会，从“奖励盒”中得到。\n各个技能所需要的技能槽数量是固定的，\n越是强力的技能就需要越多的技能槽。\n\n在社员一览中选中社员，\n在社员详情中点击已解锁的技能槽\n即可对学院偶像技能进行设置。',NULL,NULL,NULL);
INSERT INTO help_m VALUES(66,4,1,'ライブって？','什么是演唱会？',replace(replace('いろんな楽曲をプレイすることです。\r\n部活を頑張る部員たちに指示をだして、ライブを成功に導きましょう。','\r',char(13)),'\n',char(10)),'演唱众多歌曲的活动。\n请给予社员指示\n引导她们获得演唱会的成功吧。',NULL,NULL,NULL);
INSERT INTO help_m VALUES(67,4,2,'楽曲','歌曲',replace(replace('楽曲は通常楽曲と特別楽曲があります。\r\n難易度を選んで、プレイしたい楽曲をタップしましょう。\r\n楽曲は、条件を指定して表示する楽曲を並べかえたり、絞り込むことができます。\r\n\r\n・ランキング\r\n楽曲ごとにランキングがあります。SCOREが高い順に表示されています。\r\n\r\n・ステージレベル\r\n楽曲の難易度を表示しています。星の数字が大きいほど難しくなります。\r\n','\r',char(13)),'\n',char(10)),'歌曲分为普通歌曲和特别歌曲。\n选择难易度后，即可点击感兴趣的歌曲开始游戏。\n\n・排名\n每一首歌曲都拥有各自的排名。\n排名会按照玩家获得的分数从高到底显示。\n\n・难度等级\n代表歌曲的难易度。\n星星的数量越多，则表示歌曲的难度越高。',NULL,NULL,NULL);
INSERT INTO help_m VALUES(68,4,3,'通常楽曲','普通歌曲',replace(replace('メインストーリーを読んだり、特定の課題をクリアするとプレイできる楽曲です。\r\n\r\n難易度EASY・NORMAL・HARD・EXPERTがプレイできます。','\r',char(13)),'\n',char(10)),'完成主线剧情后即可解锁普通歌曲。\n可以选中容易，普通，困难\n和专家等难度。',NULL,NULL,NULL);
INSERT INTO help_m VALUES(69,4,4,'特別楽曲','特别歌曲',replace(replace('期間限定でプレイができる楽曲です。\r\n\r\n・日替わり楽曲\r\n毎日日替わりで特別な楽曲をプレイできます。\r\n\r\n・限定楽曲\r\n期間限定の楽曲がプレイできます。期間を過ぎるとプレイできなくなりますので注意してください。\r\n\r\n※AC譜面は3点以上の同時タップが必要となるなど、プレイが非常に難しい内容となっております。また、ご利用の端末やプレイ状況によっては3点以上等のタップ時に正常に反応しない場合もございます。\r\n※AC譜面は「ごほうびBOX」ゲージ増加の対象外となります。','\r',char(13)),'\n',char(10)),'在限定期间内才可进行的歌曲。\n\n・每日歌曲\n每天都会不断更新的特别歌曲。\n\n・限定歌曲\n在限定期间内才可进行的歌曲。\n\n超过限定期间后，将无法进行此类歌曲，要注意哦。',NULL,NULL,NULL);
INSERT INTO help_m VALUES(70,4,5,'MASTER楽曲プレイ機能','大师歌曲练习功能',replace(replace('ライブメニュー内の上部のボタンからご利用いただけます。\r\n\r\nMASTER楽曲プレイ機能は、MASTERチャレンジ回数とLPを消費してMASTER楽曲を練習することができます。\r\n※練習できるMASTER楽曲は日によって変わります。\r\n\r\n\r\n・MASTERチャレンジ回数\r\n1日にMASTER楽曲を練習できる回数で、毎日0:00に自動で回復します。一定までRankを上げると回数の上限が増えます。','\r',char(13)),'\n',char(10)),'点击演唱会页面上方的图示(特别歌曲右边)即可使用此功能。\n\n所谓的“大师歌曲练习功能”，\n是消耗大师歌曲挑战次数和耐力即可练习大师歌曲的功能。\n※每天可练习的大师歌曲都不同\n\n・大师歌曲挑战次数\n每天的可练习次数会在上午8:00自动回复。\n当玩家等级提升到一定程度，可练习次数的最大值就会增加。',NULL,NULL,NULL);
INSERT INTO help_m VALUES(71,4,6,'消費LPの変更','消费耐力变更',replace(replace('ライブ開始前に、消費するLPを変更することができます。\r\n消費したLPに応じて一部の報酬の量が増加します。\r\n\r\n\r\nアイコンコレクションイベントでは、消費するイベントアイコンの数を変更することもできます。\r\n\r\n消費LPの変更による報酬の量の変化は以下の通りです。\r\n\r\n（増加する報酬）\r\n・EXP\r\n・G\r\n・イベントアイコン\r\n・イベントpt\r\n・R部員の出現率\r\n・ごほうびBOXのゲージ\r\n\r\n（増加しない報酬）\r\n・絆pt\r\n・お気に入りpt\r\n・課題の進捗率\r\n・友情pt\r\n・楽曲目標のプレイ回数','\r',char(13)),'\n',char(10)),'演唱会开始前，可以进行消费耐力的变更。\n对应消费耐力一部分的报酬数量将会增加。\n\n 在传统活动中，\n也可以在这里进行消费活动道具数量的变更。\n\n消费耐力的变更以下的报酬将会变化。\n\n(会增加的报酬)\n・经验值\n・金币\n・活动道具\n・活动pt\n・稀有度R社员的出现机率\n・奖励盒的累积条\n\n(不会增加的报酬)\n・绊点数\n・喜爱点数\n・课题的完成度\n・友情pt\n・歌曲目标的游玩次数\n',NULL,NULL,NULL);
INSERT INTO help_m VALUES(72,4,7,'ゲスト','嘉宾',replace(replace('ライブを一緒に手伝ってくれる友達や学院生のことです。\r\nライブを成功させると、ゲストを選択した時に表示されている友情ptがもらえます。\r\n\r\nゲストで参加してくれた友達にも、同じように友情ptが贈られます。','\r',char(13)),'\n',char(10)),'演唱会时可以选择好友或者同学来帮忙。\n在演唱会成功时，可以获得\n选择嘉宾时显示的友情点。\n\n成为嘉宾的好友也会\n获得相同数量的友情点。',NULL,NULL,NULL);
INSERT INTO help_m VALUES(73,4,8,'リズムアイコン','节奏图标',replace(replace('画面中央の音符から出てくるアイコンのことです。\r\n楽曲のリズムにあわせて流れてきます。\r\n楽曲の難易度が高くなるとリズムアイコンの数が多くなり流れてくるスピードが速くなります。\r\n\r\n※『ライブ設定』からスピードを調整することができます。','\r',char(13)),'\n',char(10)),'从画面中央的音符发出的图标。\n当节奏图标和社员图标重叠时就要点击屏幕。\n歌曲的难度越高，节奏图标的数量就越多\n移动速度也越快。\n\n*可以在“各种设置”的“演唱会速度”中进行调整',NULL,NULL,NULL);
INSERT INTO help_m VALUES(74,4,9,'ユニットアイコン','社员图标',replace(replace('ライブに出ている部員たちのことです。\r\nライブ中の部員の配置を変更したい場合は、ユニット編成から変更できます。\r\nリズムアイコンと重なる瞬間にタップしましょう。','\r',char(13)),'\n',char(10)),'参加演唱会的社员。\n若想更改参加演唱会的社员配置\n请点击"更改队伍"。\n请在节奏图标和社员图标重叠的瞬间点击屏幕。',NULL,NULL,NULL);
INSERT INTO help_m VALUES(75,4,10,'属性','属性',replace(replace('楽曲にもスマイル、ピュア、クールの3種類の属性があります。\r\n\r\nリズムアイコンと部員の属性が一致するとSCOREが上がりやすくなります。\r\n曲の属性に合わせてユニットを編成したり、特技を持った部員をユニットに入れたりすることで、ライブがさらに有利になります。','\r',char(13)),'\n',char(10)),'歌曲也拥有甜美，清纯和洒脱共3种属性。\n\n若节奏图标和社员的属性相同\n则更容易获得分数。\n依照歌曲的属性更改队伍配置，或将拥有技能的社员\n编入队伍，都有利于演唱会的成功举行。',NULL,NULL,NULL);
INSERT INTO help_m VALUES(76,4,11,'部員パラメーター','社员信息',replace(replace('部員の能力のことです。\r\n部員詳細で見ることができます。\r\n\r\n部員のパラメーターには体力とスマイルP、ピュアP、クールP（以下属性P）の4つがあります。\r\nユニットでは9人のパラメーターが合計されます。\r\n\r\nライブでは体力は多い方が失敗しにくいです。\r\nさらに曲には属性があって、曲と同じ属性Pが高ければ高いほどライブでSCOREが上がりやすくなります。\r\n\r\n部員と絆を深めると、部員の属性Pが上がるので絆を深めて、SCOREアップを目指しましょう。\r\n\r\nセンタースキルを持っている部員の中には属性Pをアップしてくれる部員もいます。','\r',char(13)),'\n',char(10)),'可以查看社员的能力。\n在社员详情中可以看到\n\n社员信息包括体力和\n甜美，清纯，洒脱（以下统称属性）4种\n队伍能力则是9名社员的各项指数之和\n\n在演唱会中体力越多越不容易失败！\n歌曲也是有属性的，和歌曲相同的属性越高\n在演唱会中获得的分数就越多\n\n加深社员间的亲密度，社员的属性就会升高\n请和社员多加深感情，以获得更高的属性吧。\n\n某些社员的主唱技能\n也可以提高属性。',NULL,NULL,NULL);
INSERT INTO help_m VALUES(77,4,12,'タップ評価','点击评价',replace(replace('ユニットアイコンとリズムアイコンが丁度良いタイミングでタップすると、PERFECTになります。\r\n次に良いのがGREAT、次はGOODこのタイミングでタップできればSCOREがアップします。\r\n\r\nタイミングがずれているとBADになりタップができないとMISSになります。\r\n\r\nBADやMISSだと体力が減りますので気を付けましょう。','\r',char(13)),'\n',char(10)),'在社员图标和节奏图标\n相重迭的一瞬间点击成功的话，评价就是PERFECT\n其次是GREAT，然后是GOOD\n如果能抓住这些时机点击，分数就会提高！\n\n如果错过了时机就会变成BAD\n最后没能点击的话就会变成MISS\n\nBAD和MISS时会减少体力的，要小心哦。',NULL,NULL,NULL);
INSERT INTO help_m VALUES(78,4,13,'長押しリズムアイコン','长按节奏图示',replace(replace('リズムアイコンが白い帯でつながったものは「長押しリズムアイコン」と言います。\r\n\r\n長押しマークが外れるまで押し続けましょう。\r\n途中で離したり、離すタイミングが遅いと、BADやMISSになってしまうので気を付けましょう。','\r',char(13)),'\n',char(10)),'节奏图示中有一种会拖着白色的长条\n这就是长按节奏图示\n\n要一直按到长按标志结束\n如果中途放开的话\n就会变成Bad或MISS，要小心哦。',NULL,NULL,NULL);
INSERT INTO help_m VALUES(79,4,14,'同時押しリズムアイコン','同时点击节奏图示',replace(replace('複数のリズムアイコンの真ん中に、銀色の横棒が入っているアイコンが「同時押しリズムアイコン」です。\r\n\r\n部員と重なった瞬間、同時にタップしないとBADになるので気を付けましょう。','\r',char(13)),'\n',char(10)),'节奏图示中央有银色的横线时，\n这就是需要同时点击的节奏图示。\n\n当和社员相重迭的瞬间，就要同时点击\n否则就会变成BAD，要注意哦。',NULL,NULL,NULL);
INSERT INTO help_m VALUES(80,4,15,'スターアイコン','星星图示',replace(replace('リズムアイコンの中に星のマークが入っているアイコンは「スターアイコン」と言います。\r\n\r\nPERFECTかGREATのタイミングでタップしないと、体力が減ってしまうため気を付けてください。','\r',char(13)),'\n',char(10)),'在节奏图标中\n会有星星的标志出现，这就是星星图示\n\n如果不能在PERFECT或者GREAT的时机按下的话\n体力就会减少。',NULL,NULL,NULL);
INSERT INTO help_m VALUES(81,4,16,'スイングアイコン','滑动图标',replace(replace('リズムアイコンの中に矢印のマークが入っているアイコンは「スイングアイコン」と言います。\r\n始点のアイコンから終点のアイコンまでをなぞることで判定されます。\r\nスイングアイコンは、タップ判定が通常のアイコンより広くなっています。\r\nスイングアイコンの1アイコンで獲得できるスコアは通常のリズムアイコンの半分になります。','\r',char(13)),'\n',char(10)),'中间带有箭头标志的节奏图标\n称作“滑动图标”。\n从起点标志滑动至终点标志\n即可完成判定。\n滑动图标的点击判定范围比普通的图标\n要更大一些。\n通过滑动图标可获得的SCORE数\n是普通节奏图标的一半。',NULL,NULL,NULL);
INSERT INTO help_m VALUES(82,4,17,'イベントアイコン','活动图示','イベント開催中に出てくる特別なアイコンです。','在活动期间出现的特殊图标。',NULL,NULL,NULL);
INSERT INTO help_m VALUES(83,4,18,'SCORE','分数',replace(replace('ライブ画面真ん中にあるあなたがライブで獲得した得点のことです。\r\n\r\nタップ評価が良いと上昇しやすくなります。\r\nSCOREに合わせてSCORE RANKゲージも上昇します。','\r',char(13)),'\n',char(10)),'显示在演唱会界面的正中央\n代表你在演唱会中获得分数。\n\n点击评价高的话，分数也会更容易上升。\n根据分数等级条也会上升。',NULL,NULL,NULL);
INSERT INTO help_m VALUES(84,4,19,'SCORE RANK','分数等级',replace(replace('SCORE RANKはライブで獲得したSCOREの値によって決まります。\r\n\r\nS，A，B，Cと4段階ありSCOREが良い程、SCORE RANKも良くなります。\r\n\r\nSCORE RANKがC以上だと、ボーナスで獲得できるGやEXPが増えたりしますので、C以上を目指しましょう。\r\n\r\nライブ中はSCORE RANKゲージで今のSCORE RANKがわかります。','\r',char(13)),'\n',char(10)),'等级是根据在演唱会中获得的分数来决定的。\n\n一共分为S、A、B、C、D，5个等级，\n分数越高，等级也就越高\n\n如果等级达到C以上，\n可以获得额外的G及经验的加成，努力达到C以上吧。\n\n演唱会中可以通过等级条知道现在的评价哦！',NULL,NULL,NULL);
INSERT INTO help_m VALUES(85,4,20,'SCORE RANKゲージ','分数等级条',replace(replace('ライブ画面上にあるライブのSCORE RANKを示すゲージです。\r\nSCOREが上がるとSCORE RANKゲージも上がります。\r\n\r\nライブ中はSCORE RANKゲージで今のSCORE RANKがわかります。\r\n\r\n途中で体力がなくなってもコンティニューすればSCORE RANKゲージとSCOREはそのままでライブを再開することができます。','\r',char(13)),'\n',char(10)),'演唱会界面中显示有等级条\n当分数上涨时等级条也会增长。\n\n在演唱会中，通过等级条\n即可知道当前的等级。\n\n即使在中途体力耗尽，只要选择继续游戏\n等级和分数就会维持当前的状态\n并继续进行演唱会。',NULL,NULL,NULL);
INSERT INTO help_m VALUES(86,4,21,'体力','体力',replace(replace('画面左上にある、ライブに参加している部員たちの体力のことです。\r\n\r\nMISSやBAD、スターアイコンで失敗すると体力がへって、体力が全部なくなると、ライブ失敗になります。','\r',char(13)),'\n',char(10)),'画面左上方显示了参加演唱会的所有社员的体力。\n\n当出现MISS、BAD，或在星星图示上失败的话\n体力就会减少，当体力归零时，演唱会也就失败了',NULL,NULL,NULL);
INSERT INTO help_m VALUES(87,4,22,'成功と失敗','成功和失败',replace(replace('ライブが無事成功すると、GやEXPがもらえます。\r\n\r\n失敗しても、ラブカストーンを1個使ってコンティニューすることができます。','\r',char(13)),'\n',char(10)),'演唱会成功后可以获得G及经验。\n\n即使失败，只要消耗1颗爱心\n就可以继续游戏。',NULL,NULL,NULL);
INSERT INTO help_m VALUES(88,4,23,'楽曲目標','歌曲目标',replace(replace('楽曲ごとに用意された目標のことです。\r\n目標はRANK、COMBO、クリア回数の3タイプそれぞれ4つの難易度があります。\r\n\r\n見事目標を達成すると、トロフィーマークが現れ、それぞれの報酬を獲得することができます。','\r',char(13)),'\n',char(10)),'每首歌曲都有不同的目标。\n目标共有等级、连击、通关次数3种\n分别有4个难易度\n\n达成目标后，将会出现奖章标志的图案\n并获得不同种类的奖励。',NULL,NULL,NULL);
INSERT INTO help_m VALUES(89,4,24,'ライブが成功しない','演唱会失败',replace(replace('練習で部員のレベルをあげたりライブの属性にあったユニットを編成するとライブが成功しやすくなるかもしれません。\r\n\r\nライブの属性は、ライブ楽曲選択画面で見ることができます。','\r',char(13)),'\n',char(10)),'通过练习提升社员的等级\n或者编组符合演唱会属性的队伍\n都可以提高演唱会成功的机率。\n\n演唱会的属性可以通过歌曲的选择界面查看。',NULL,NULL,NULL);
INSERT INTO help_m VALUES(90,4,25,'転入生','新社员',replace(replace('ライブでは入部希望者がやってきて、ライブに成功すると部員になってくれます。\r\n※入部希望者は、一般生勧誘への追加から3ヶ月経過した転入生が対象となります。','\r',char(13)),'\n',char(10)),'进行演唱会的话，会出现希望加入学园偶像的新社员\n当演唱会成功时，新社员就会加入。',NULL,NULL,NULL);
INSERT INTO help_m VALUES(92,4,27,'ごほうびBOX','奖励盒',replace(replace('ごほうびBOXはライブクリア後に出現します。\r\n獲得したごほうびBOXを開けるには、ライブをクリアしてゲージをためる必要があります。\r\nごほうびBOXからはSIスキルなどのアイテムが獲得できます。\r\n\r\nごほうびBOXにはランクがあり、ランクの高いものほど良いアイテムが出やすい反面、開くために必要なゲージも多くなります。\r\n\r\nまた、一定確率で「スペシャルごほうびBOX」が出現することもあります。\r\n出現した際は、挑戦中の「ごほうびBOX」と「スペシャルごほうびBOX」のどちらに挑戦するかを選んで頂きます。\r\n※挑戦中の通常ごほうびBOXはあとから挑戦可能です。\r\n\r\n「スペシャルごほうびBOX」はμ''sやAqours、また各メンバーにちなんだBOXが用意されており、メンバー称号などを入手することが可能です。','\r',char(13)),'\n',char(10)),'完成演唱会后会出现奖励盒。\n想要开启奖励盒的话，\n就必须完成演唱会并积累进度条。\n从奖励盒中\n可以获得学院偶像技能以及其它道具。\n\n奖励盒存在不同等级，\n等级越高，就越容易开出好的道具，\n相对的，开启奖励盒所需的进度条也会变长。\n\n如果不开启手上的奖励盒\n新的奖励盒就不会出现。',NULL,NULL,NULL);
INSERT INTO help_m VALUES(93,4,28,'ランダムセレクト','随机选择',replace(replace('解禁済みの通常楽曲の中から、自動で選曲されるライブです。\r\n一定数以上の通常楽曲が解禁された後、特別楽曲からランダムセレクトをプレイすることができます。\r\n\r\nランダムセレクトの楽曲は、スマイル、ピュア、クールの楽曲が日替わりで登場します。\r\n\r\nランダムセレクトのライブでは、ごほうびBOXを開けるためのゲージがたまりやすくなります。','\r',char(13)),'\n',char(10)),'自动从已经解锁的常规乐曲中进行选曲的演唱会。<br /><br />常规乐曲解锁到一定程度以上，<br /><br />就能游玩随机选取的特殊乐曲。<br /><br /><br /><br />随机选取的乐曲<br /><br />会按甜美、清纯、洒脱的顺序每日更换。<br /><br /><br /><br />随机选取的乐曲更容易积蓄用于开启<br /><br />奖励箱的累积槽。',NULL,NULL,NULL);
INSERT INTO help_m VALUES(94,4,29,'μ''s・Aqours切り替え','μ''s/Aqours切换',replace(replace('μ''sとAqoursのライブメニューを切り替えます。\r\n切り替えると、ストーリーや勧誘などにも反映されます。','\r',char(13)),'\n',char(10)),'可以切换μ''s和Aqours的演唱会菜单。\n切换之后，剧情和社员招募等方面都会发生相应变化。',NULL,NULL,NULL);
INSERT INTO help_m VALUES(95,4,30,'うでまえチェック','演唱会实力确认',replace(replace('以下の画面でリズムアイコンのタップ結果の詳細が確認できるようになります。\r\n\r\n・「ライブリザルト」画面\r\nライブプレイ時のタップ結果の詳細が表示されます。\r\n\r\n・「うでまえチェック」画面\r\n楽曲ごとに最もPERFECT数を多く獲得した際のプレイ結果が表示されます。「うでまえチェック」画面へは「ライブ」画面中の青いアイコン(画面左下)から移動可能になります。\r\n\r\n※「うでまえチェック」機能は「通常ライブ」と「アイコンコレクション(特殊アイコン収集イベント)」のみの対応となります。','\r',char(13)),'\n',char(10)),'在下列画面中可以确认到节奏图标的点击分数详情。\n\n・“演唱会结果”画面\n会显示进行演唱会时点击节奏图标的得分详细信息。\n\n・“演唱会实力确认”画面\n会显示每首歌曲获得最多PERFECT数量时\n的游玩成绩。\n\n可以从“演唱会”画面左下方的蓝色图示进到\n“演唱会实力确认”画面。\n\n※“演唱会实力确认”功能仅适用于“一般演唱会”\n及“道具图标(特殊节奏图标)收集类型的活动”。',NULL,NULL,NULL);
INSERT INTO help_m VALUES(96,4,31,'ライブリプレイ','演唱会重播',replace(replace('「うでまえチェック」画面からライブリプレイを見ることができます。\r\n\r\nライブプレイを振り返ることができ、画面の上部のグラフで、上手くいったところ、ミスをしたところなどが確認できるようになっています。','\r',char(13)),'\n',char(10)),'可以自“演唱会实力确认”画面观看演唱会重播。\n\n透过重播功能可以回顾该场演唱会，\n也可以透过画面上方的曲线图，\n确认自己玩得比较熟练的地方和出现失误的地方等等。',NULL,NULL,NULL);
INSERT INTO help_m VALUES(97,5,2,'特待生勧誘','优等生招募',replace(replace('レアリティがR，SR，SSR，URの部員を勧誘することができます。\r\n\r\n・1回勧誘\r\n部員を1人勧誘できます。\r\n\r\n・11回勧誘\r\n部員を11人勧誘できます。\r\nSR以上の部員を必ず1人勧誘できます。\r\n\r\n勧誘に必要なアイテムは、勧誘を行う時に確認できます。\r\n\r\n特待生勧誘に部員が追加された翌月に、部員のレアリティに応じて以下の勧誘にも部員が追加されます。\r\n\r\n・補助チケット勧誘\r\nSR・UR勧誘\r\nSSR・UR勧誘\r\nUR勧誘\r\n\r\n・その他の勧誘\r\nSR以上確定勧誘\r\nSR以上確定3連勧誘\r\nSSR以上確定勧誘\r\nUR1枚確定11連勧誘\r\nUR確定勧誘','\r',char(13)),'\n',char(10)),'可以招募稀有度为R，SR，SSR，UR的社员。\n\n・1次招募\n消费5颗爱心进行1次招募。\n\n・11连招募\n消费50颗爱心进行11次招募。\n一定能招募到至少1位稀有度为SR或以上的社员。',NULL,NULL,NULL);
INSERT INTO help_m VALUES(98,5,3,'限定勧誘','招募限时社员',replace(replace('期間限定で特別な部員を勧誘することができます。\r\n\r\n・1回勧誘\r\n部員を1人勧誘できます。\r\n\r\n・11回勧誘\r\n部員を11人勧誘できます。\r\nSR以上の部員を必ず1人勧誘できます。\r\n\r\n勧誘に必要なアイテムは、勧誘を行う時に確認できます。','\r',char(13)),'\n',char(10)),'可以在限定期间内招募特别社员。\n可以招募稀有度为R、SR、UR的社员。\n\n・1次招募\n消耗5颗爱心进行1次招募。\n\n・11连招募\n消耗50颗爱心进行11次招募。\n在限定期间内，一定能招募到至少1位稀有度为SR以上的社员。',NULL,NULL,NULL);
INSERT INTO help_m VALUES(99,5,8,'特待生ボーナス','优等生奖励',replace(replace('特待生勧誘や限定勧誘を1回するごとに、特待生ボーナスが1つたまります。\r\n特待生ボーナスのゲージがMAXになると補助チケットを1枚もらえます。\r\n\r\n※補助チケットは直接補助チケット勧誘ページへ付与されます。','\r',char(13)),'\n',char(10)),'进行1次特待生招募就可以获得1点特待生奖励。\n\n蓄满特待生奖励槽，\n就可以获得1张辅助券。\n\n可以在辅助券招募页面\n查看当前的辅助券持有数。\n\n※辅助券会直接追加至辅助券招募页面。\n※升级奖励中获得的辅助券\n 会被送至礼品箱。',NULL,NULL,NULL);
INSERT INTO help_m VALUES(100,5,10,'勧誘チケット','招募券',replace(replace('勧誘チケット1枚で1回、特待生勧誘や限定勧誘ができます。\r\n勧誘チケットは、イベントの上位報酬としてもらえることがあります。','\r',char(13)),'\n',char(10)),'消费1张招募券可以进行1次优等生招募。\n招募券可通过\n活动的高级奖励获得。',NULL,NULL,NULL);
INSERT INTO help_m VALUES(101,5,11,'一般生勧誘','招募普通生',replace(replace('友情ptを使って、レアリティがN，R，SSRの部員を勧誘できます。\r\n1日1回は無料で勧誘できます。\r\n\r\nさらに一般生勧誘では、曜日ごとに対象の部員の出現率がアップします。\r\n出現率アップ対象の部員は、毎日0:00に変更が行われます。\r\n※日曜日は出現率アップの対象外です。\r\n\r\n出現率アップ対象の部員と提供割合の詳細は、勧誘画面左下の「詳細」をタップしてご確認ください。','\r',char(13)),'\n',char(10)),'使用友情点可招募稀有度为N，R的社员。\n每天可免费招募1次。',NULL,NULL,NULL);
INSERT INTO help_m VALUES(102,5,12,'友情pt','友情点',replace(replace('友情ptは、一般生勧誘をする時に必要なポイントです。\r\nゲストと一緒にライブをしたり、楽曲目標をクリアするとたまります。\r\n友達からゲストに選択された時ももらうことができます。','\r',char(13)),'\n',char(10)),'友情点在招募普通生时会消耗。\n与嘉宾一起举行演唱会\n或达成歌曲的目标都可获得友情点。\n被好友选为对方的嘉宾时也可获得友情点。',NULL,NULL,NULL);
INSERT INTO help_m VALUES(103,5,13,'補助チケット勧誘','辅助券招募',replace(replace('補助チケットを使ってできる勧誘のことです。\r\n\r\n・SR・UR勧誘\r\n補助チケット5枚でSR部員とUR部員のみを勧誘することができます。\r\n\r\n・SSR・UR勧誘\r\n補助チケット10枚でSSR部員とUR部員のみを勧誘することができます。\r\n\r\n・UR勧誘\r\n補助チケット25枚でUR部員のみを勧誘することができます。\r\n\r\n・特技アップサポートメンバー勧誘\r\n補助チケット1枚で特技Lvを上げるのに役立つ特技アップサポートメンバーを勧誘することができます。','\r',char(13)),'\n',char(10)),'消费辅助招募券进行招募。\n\n・SR・UR招募\n消费5张辅助招募券\n招募稀有度为SR或UR的社员。\n\n・技能提升支援社员招募\n消费1张辅助招募券\n招募能够提升技能等级的\n技能提升支援社员。',NULL,NULL,NULL);
INSERT INTO help_m VALUES(104,5,14,'補助チケット','辅助招募券',replace(replace('補助チケットを使って特別な勧誘ができます。\r\n\r\n補助チケットはRankが20上がるごとに1枚もらえます（プレゼントボックスへ送られます）。\r\n特待生勧誘や限定勧誘でたまる特待生ボーナスのゲージがMAXになった時も、1枚もらえます。\r\n\r\n※持っている補助チケットの枚数は補助チケットの勧誘ページで確認できます。','\r',char(13)),'\n',char(10)),'可使用辅助券进行的招募。\n\n·SR·UR招募\n使用5张辅助券\n可招募SR社员和UR社员。\n\n·SSR·UR招募\n使用10张辅助券\n可招募SSR社员和UR社员。\n\n·UR招募\n使用25张辅助券\n可招募UR社员。\n\n·技能UP支援成员招募\n使用1张辅助券\n可招募能够提升技能等级\n的支援成员。',NULL,NULL,NULL);
INSERT INTO help_m VALUES(105,6,1,'LP回復','恢复耐力',replace(replace('ラブカストーンやアイテムを使って、LPを回復できます。\r\n\r\n最大LPが240未満の場合は、LPが240以上になるまで回復できます。\r\n\r\n・ラブカストーン\r\n1個でその時のRankの最大LP分のLPを回復できます。\r\n\r\n・シュガーキューブ[LP50]\r\n1個でLPを50回復できます。\r\n\r\n・シュガーポット[LP50％]\r\n1個でその時のRankの最大LP50％分のLPを回復できます。\r\n\r\n・シュガーポット[LP100％]\r\n1個でその時のRankの最大LP分のLPを回復できます。\r\n\r\n右上の『LP回復』ボタン、または「メニュー＞アイテムリスト」からも使用することができます。','\r',char(13)),'\n',char(10)),'可以使用1个爱心恢复耐力。\n\n・ \n使用1颗 可以回复与当下Rank相对应最大值的耐力。\n\n・糖浆[耐力5]\n使用1个可以回复5点耐力。\n\n・方糖[耐力50]\n使用1颗可以回复50点耐力。\n\n・糖罐[耐力50%]\n使用1个可以回复与当下等级相对应最大值50%的耐力。\n\n・糖罐[耐力100%]\n使用1个可以回复与与当下等级相对应最大值的耐力。',NULL,NULL,NULL);
INSERT INTO help_m VALUES(106,6,2,'ラブカストーン購入','购买钻石',replace(replace('ラブカストーンを購入することができます。\r\nまとめ買いするとお得です。\r\n\r\nラブカストーンについては『ヘルプ』の『ホーム』に詳細が記載されています。','\r',char(13)),'\n',char(10)),'钻石是可以购买的\n一次性购买越多，优惠也会越多。\n\n钻石可以用来兑换爱心',NULL,NULL,NULL);
INSERT INTO help_m VALUES(107,6,3,'部員数の上限を増やす','增加社员上限',replace(replace('部員数の上限を増やすことができます。\r\nラブカストーン1個で4人増やせます。','\r',char(13)),'\n',char(10)),'可以增加社员上限值。<br /><br />1个爱心可以增加4个人。',NULL,NULL,NULL);
INSERT INTO help_m VALUES(108,6,4,'セットアイテムの販売','礼包购买',replace(replace('様々なアイテムが入ったセット商品などを購入できます。\r\nショップに表示されているバナー上の「詳細」をタップすると、商品の内容を確認することができます。\r\n\r\n価格は販売開始時の価格です。為替レート変動に伴い今後変更となる可能性もありますのでご了承ください。','\r',char(13)),'\n',char(10)),'在商店中可以购买包含了各式道具在其中的礼包。\n点击商店中的横幅图示，\n就可以确认商品的详细内容。',NULL,NULL,NULL);
INSERT INTO help_m VALUES(110,7,1,'ライブ設定','演唱会设置',replace(replace('ライブに関する各種設定変更ができます。\r\n\r\n・アイコン\r\nカスタマイズをしたりタイミング調整ができます。\r\n\r\n・タップ音\r\nタップ音を変更することができます。\r\n\r\n・ライブ画面\r\nライブ中演出のON/OFFが設定できます。\r\n\r\n・ライブ音量\r\nライブ中のタップ音、特技、楽曲の音量が調整できます。\r\n\r\n・ライブスピード\r\n各難易度によってスピードが調整できます。\r\n\r\n・うでまえチェック\r\n機能のON/OFFが設定できます。','\r',char(13)),'\n',char(10)),'可更改与演唱会相关的各种设置。\n\n・图标\n可进行自定义并调整点击时机 \n\n・点击音效\n可更改点击音效\n*预定于今后追加\n\n・演出画面\n可开启/关闭演唱会中的演出效果\n\n・演唱会音量\n可调整演唱会中的点击音效、特技、歌曲等的音量\n\n・演唱会速度\n可调整各难度下的演唱会速度\n\n・结果查看 \n可开启/关闭该功能',NULL,NULL,NULL);
INSERT INTO help_m VALUES(111,7,2,'ライブ画面','演唱会',replace(replace('・カットイン\r\nライブ中の部員のカットインの設定をすることができます。\r\n\r\n・エフェクト\r\nライブ中のエフェクトの設定をすることができます。\r\n\r\n・文字サイズ\r\nライブ中のコンボ数や判定の文字の大きさを設定できます。\r\n\r\n・タイミング調整\r\nライブ中のタップ反応タイミングを調整することができます。\r\n\r\n・リズムアイコン\r\nライブ中に流れるリズムアイコンのデザインを選択することができます。','\r',char(13)),'\n',char(10)),'·特写\n 可以设置演唱会中社员的特写镜头。\n\n·特效\n 可以设置演唱会中的特效镜头。\n\n·字体大小\n 可以设置演唱会中显示连击数和判定的字体大小。\n\n·时间调整\n 可以调整演唱会中点击时的反应时间。',NULL,NULL,NULL);
INSERT INTO help_m VALUES(112,7,3,'ライブスピード','演唱会速度',replace(replace('ライブ中のリズムアイコンが流れてくるスピードを難易度ごとに調整することができます。\r\n\r\n「デフォルト」ボタンを押すと、各難易度標準のスピードに戻ります。','\r',char(13)),'\n',char(10)),'可以对各个难度下\n演唱会中的节奏图标移动速度进行调整。\n\n点击“默认”按钮的话\n就可以将各难度的速度重置为标准速度。',NULL,NULL,NULL);
INSERT INTO help_m VALUES(113,7,4,'アルバム','相册','今まで仲間になったことのある部員を、確認することができます。','可以查看至今为止拥有过的所有社员。',NULL,NULL,NULL);
INSERT INTO help_m VALUES(114,7,5,'アルバムの部員詳細','相册的社员资料','部員の詳しいプロフィールを確認できます。','可以查看社员的详细资料。',NULL,NULL,NULL);
INSERT INTO help_m VALUES(115,7,6,'アイテムリスト','道具一览',replace(replace('所持しているアイテムの内容と所持数を確認したり、アイテムを使用することができます。\r\n\r\n・回復アイテム\r\n所持しているLP回復アイテムを確認できます。\r\n\r\n・効果アイテム\r\n所持している効果アイテムを確認できます。\r\n\r\n・その他\r\n「回復アイテム」と「効果アイテム」を除く、所持する下記アイテムを確認できます。\r\n・勧誘チケット\r\n・イベントストーリー解放キー\r\n・RANKアップジュエル\r\n※μ''sモードとAqoursモードの両方で使用可能な勧誘チケットの場合、「使用する」ボタンを押すと現在選択しているモードの勧誘へ移動します。\r\n※RANKアップジュエルは現在のRankにおいて使用可能な場合、「使用」ボタンから使用することで、Rankを上昇させることができます。\r\n※RANKアップジュエルは規定数の同じRANKアップジュエルとGを消費し、「強化」ボタンから1段階上のアイテムに強化することが可能です。','\r',char(13)),'\n',char(10)),'从“道具一览”页面可以确认目前持有的道具跟持有数，\n也可以直接使用道具。\n\n・“恢复道具”页面\n 可以确认持有的耐力恢复道具。\n\n・“特效道具”页面\n 可以确认持有的效果道具。\n\n・“其他”页面\n 可以确认持有的招募券和活动剧情解锁钥匙。\n ※按下可以选择要招募μ''s成员或Aqours成员的招募券上的\n 「使用」按钮时，会依照目前自己是处于μ''s或Aqours模式\n 而导到该模式的招募页面。',NULL,NULL,NULL);
INSERT INTO help_m VALUES(116,7,7,'効果アイテム','特效道具',replace(replace('一定時間または一定回数、特定の報酬を増加させることができます。\r\n\r\n「アイテムリスト」の「効果アイテム」より効果アイテムに関する以下の操作を行えます。\r\n・所持している効果アイテムとその内容の確認\r\n・効果アイテムの使用\r\n・使用後の残り時間・回数の確認\r\n\r\n効果アイテムにはさまざまな種類がありますが、代表的なアイテムは以下の3種類です。\r\n\r\n・EXPアップのお守り\r\n使用してから60分の間だけ、ライブでの獲得EXPを25%アップさせます。\r\n\r\n・Gアップのお守り\r\n使用してから60分の間だけ、ライブでの獲得Gを25%アップさせます。\r\n\r\n・絆Ptアップのお守り\r\n使用してから60分の間だけ、ライブでの獲得絆Ptを25%アップさせます。\r\n\r\n※メンテナンス前に効果アイテムを使用した場合、メンテナンス中も効果時間が経過しますのでご注意ください。\r\n','\r',char(13)),'\n',char(10)),'使用后，可以在固定期间或固定次数\n获得较多的特定报酬。\n\n“道具一览”的"特效道具”页面有以下几项功能。\n・确认持有的特效道具及道具内容\n・使用特效道具\n・确认使用道具后的剩余时间、次数\n\n※请注意，如果在系统维护前使用特效道具，\n在系统维护期间仍会消耗道具的可使用时数。',NULL,NULL,NULL);
INSERT INTO help_m VALUES(117,7,8,'スタンプカスタマイズ','编辑表情组合',replace(replace('スタンプとはつげんの設定・確認ができます。\r\n\r\nスタンプリストとはつげんリストをカスタマイズし、マイセットに登録することができます。\r\nマイセットは5つまで登録可能です。\r\nマイセットをメインに設定することで、イベント中に表示させることができます。\r\n\r\nイベントトップからカスタマイズ画面に移動することも可能です。','\r',char(13)),'\n',char(10)),'可以设定&确认表情及信息。\n\n玩家能从表情列表及信息列表中挑选、\n编辑出专属于自己的表情组合。\n最多可以保存5组表情组合。\n将自己最喜欢的组合设定为惯用表情，\n游玩活动时就会显示该组表情。\n\n也能从活动首页跳转到表情组合编辑画面。',NULL,NULL,NULL);
INSERT INTO help_m VALUES(118,7,9,'プロフィール','个人资料',replace(replace('あなたが今までライブを成功させた楽曲の種類や、お気に入り部員のランキングを確認できます。\r\nパートナーに設定したり、ユニットを組んでライブをしたりすることでランキングに表示されるようになります。\r\n自己紹介を書くこともできます。','\r',char(13)),'\n',char(10)),'可以查看至今为止在演唱会中成功演唱过的歌曲\n以及与社员的亲密度。\n在设定同伴，\n组建队伍进行演唱会等活动时，\n会显示在排名上。\n也可以编辑自己的个人介绍。',NULL,NULL,NULL);
INSERT INTO help_m VALUES(119,7,10,'称号','称号',replace(replace('獲得した称号を確認・設定することができます。\r\n称号は特定の課題を達成することで獲得することができます。\r\n\r\nホーム画面の左上にある称号をタップすることでも利用できます。\r\n※一度獲得した称号は、再度獲得することができません。','\r',char(13)),'\n',char(10)),'可以查看、设定已获得的称号。\n完成特定课题就可以获得称号。\n\n也可以点击主界面左上方的称号按键\n进行使用。',NULL,NULL,NULL);
INSERT INTO help_m VALUES(120,7,11,'SIスキル','SI技能',replace(replace('セットすることで部員をさらに強化できる特別なスキルです。\r\n\r\n【通常SIスキル】\r\nSIスキル（スクールアイドルスキル）はライブをプレイすることで「ごほうびBOX」から獲得できます。\r\nそれぞれセットに必要なスロット数が決まっていて、\r\n強力なものほどたくさんのスロットが必要になります。\r\n\r\n部員リストで部員を選択し、\r\n部員詳細の中の解放済みスロットをタップすると\r\nSIスキルをセットすることができます。\r\n\r\n【ライブ♪アリーナ専用SIスキル】\r\nライブ♪アリーナ内でのライブでのみ効果があらわれるSIスキルです。\r\n一部の勧誘から特定の部員にセットされた状態で獲得できます。\r\nその他、ライブ♪アリーナ内の様々な場面で報酬として獲得できます。\r\nライブ♪アリーナ専用SIスキルは着脱が可能です。\r\nまた、ライブ♪アリーナ専用SIスキルは、指定数の同一スキルを使い、強化することができます。 \r\n※ライブ♪アリーナ専用SIスキルの効果は、今後のライブ♪アリーナでも同効果で使用できます。 \r\n※ライブ♪アリーナ以外ではライブ♪アリーナ専用SIスキルの効果は発揮されません。','\r',char(13)),'\n',char(10)),replace(replace('这是装备后进一步强化社员的特殊技能。\r\n\r\n【普通SI技能】\r\nSI技能（学园偶像技能）可以通过举办演唱会，在“奖励BOX”中获得。\r\nSI技能有各自所需的技能槽数，\r\n技能越强大，需要的技能槽数越多。\r\n\r\n在社员列表中选择社员，\r\n点击社员详情中的已解锁技能槽，\r\n就可以装备SI技能了。\r\n\r\n【演唱会♪竞技场专用SI技能】\r\n这是只能在演唱会♪竞技场的演唱会中发挥效果的SI技能。\r\n可在部分招募中，已装备在特定社员身上的状态获得。\r\n另外也可以在演唱会♪竞技场各种场景的奖励中获得。\r\n演唱会♪竞技场专用SI技能可自由装备/移除。\r\n此外，演唱会♪竞技场专用SI技能可以通过一定数量的相同技能来强化。 \r\n※演唱会♪竞技场专用SI技能的效果在以后的演唱会♪竞技场中也能用于发挥相同的效果。 \r\n※演唱会♪竞技场专用SI技能不会在演唱会♪竞技场以外发挥效果。','\r',char(13)),'\n',char(10)),NULL,NULL,NULL);
INSERT INTO help_m VALUES(121,7,12,'ランキング','排行榜',replace(replace('累計SCOREの個人ランキングが確認できます。\r\nあなたの順位も確認できます。\r\n\r\n※当日AM0:00からのライブプレイにおける合計SCOREが表示されます。\r\n※前日分のランキングも確認できます。','\r',char(13)),'\n',char(10)),'可以确认分数的个人排名\n还可以查看你的排名。\n\n*会显示每天凌晨0:00以后进行过的演唱会\n的总计分数排名。\n*也可以查看上一天的排名情况。',NULL,NULL,NULL);
INSERT INTO help_m VALUES(122,6,6,'シールSHOP','贴纸商店',replace(replace('R以上のレアリティの部員(※)を転部させた時や、練習相手に選んだ時に手に入る「シール」を集めて、部員やアイテムと交換できます。\r\n\r\n※特典として入手可能な部員、アルパカなどのサポートメンバー、限定ログインボーナスで獲得した部員、一般生勧誘で入手可能なSSR部員、イベント報酬UR部員、一部の交換チケットでのみ入手可能な部員、コラボイベントでの配布部員は対象外です。\r\n※「特別練習」ではシールを獲得することができません。\r\n\r\nシールは、転部対象・練習相手となる部員が覚醒しているかどうかにかかわらず1枚獲得できます。\r\n\r\n・UFシール\r\n特定のUR部員を転部または練習するか、特定のUR部員を使用してアクセサリーをメイクすることで獲得できます。\r\nUFシールを獲得できる部員については<a href="/webview.php/ufSeal/index">こちら</a>からご確認いただけます。\r\n\r\n・Uシール\r\nURの部員を転部対象・練習相手にすることで獲得できます。\r\n\r\n・SSシール\r\nSSRの部員を転部対象・練習相手にすることで獲得できます。\r\n\r\n・Sシール\r\nSRの部員を転部対象・練習相手にすることで獲得できます。\r\n\r\n・シール\r\nR部員を転部対象・練習相手にすることで獲得できます。\r\n\r\nまた、シールSHOPでは毎月10日0時にμ''s、毎月25日0時にAqoursの一部ラインナップの変更があります。','\r',char(13)),'\n',char(10)),'收集让稀有度R以上的社员退部<br /><br />或选择其陪同练习时获得的“贴纸”，<br /><br />就可用于交换道具。<br /><br /><br /><br />※可作为特典获得的社员、<br /><br /> 羊驼等辅助社员及<br /><br /> 作为限定登录奖励获得的社员除外<br /><br />※“特殊练习”无法获得贴纸<br /><br /><br /><br />贴纸共分四种，<br /><br />退部、陪同练习对象<br /><br />无论有无觉醒，都只能获得1张。<br /><br /><br /><br />·U贴纸<br /><br /> 将UR社员选作退部、陪同练习对象时可以获得的贴纸。<br /><br /><br /><br />·SS贴纸<br /><br /> 将SSR社员选作退部、陪同练习对象时可以获得的贴纸。<br /><br /><br /><br />·S贴纸<br /><br /> 将SR社员选作退部、陪同练习对象时可以获得的贴纸。<br /><br /><br /><br />·贴纸<br /><br /> 将R社员选作退部、陪同练习对象时可以获得的贴纸。',NULL,NULL,NULL);
INSERT INTO help_m VALUES(123,7,13,'背景設定','背景設定',replace(replace('獲得した背景を確認・設定することができます。\r\n背景は以下の方法で獲得することができます。\r\n\r\n・特定の課題を達成\r\n・ログインボーナス\r\n・スペシャルごほうびBOX\r\n・イベント報酬','\r',char(13)),'\n',char(10)),'可以查看、设定已获得的背景。\n完成特定课题就可以获得背景。',NULL,NULL,NULL);
INSERT INTO help_m VALUES(125,7,14,'各種設定','兑换码',replace(replace('ゲームに関する各種設定変更ができます。\r\n\r\n・名前変更\r\n・誕生日の入力\r\n・引き継ぎパスワードの入力\r\n・スクフェスIDの登録\r\n・テキストの速度変更\r\n・音量変更\r\n・データダウンロードに関する設定\r\n・プッシュ通知のON/OFF','\r',char(13)),'\n',char(10)),'随机的16位英文字母或数字构成。\n输入兑换码可获得特典社员或道具等奖励。\n\n请通过下方显示的键盘输入兑换码。\n请注意不要输错哦。',NULL,NULL,NULL);
INSERT INTO help_m VALUES(134,7,20,'DL','数据下载',replace(replace('ストーリーや楽曲の追加データをダウンロードできます。\r\nダウンロードしておくことでストーリーや楽曲が解禁された時の通信回数を減らし、快適にプレイすることができます。\r\n後からダウンロードすることもできます。\r\n\r\n部員DL設定変更では、部員に関するデータのダウンロード方法について設定の切り替えができます。','\r',char(13)),'\n',char(10)),'可以下载剧情、歌曲等追加数据。\n通过自行下载数据，\n就能够减少剧情或歌曲解锁时产生的数据传输次数，从而更加流畅地进行游戏。\n也可以选择之后再下载数据。\n\n在社员下载设置中，\n可以更改社员相关数据\n的下载方式。',NULL,NULL,NULL);
INSERT INTO help_m VALUES(135,7,21,'プッシュ通知','推送通知',replace(replace('プッシュ通知とはLPの回復やイベント情報などを端末上に表示してお知らせする機能です。\r\n通知設定は「その他＞各種設定」からいつでも変更できます。\r\n\r\n※端末OSで設定をOFFにしていると、ゲーム内でプッシュ通知をONにしていてもOFF扱いになります。\r\n','\r',char(13)),'\n',char(10)),replace(replace('推送通知指的是在设备上显示恢复耐力或活动信息等通知的功能。\r\n通知设置可以前往“其他＞各种设置”随时变更。\r\n\r\n※如果在终端操作系统中设置为关闭，即使在游戏中打开推送通知，也会被视为关闭。','\r',char(13)),'\n',char(10)),NULL,NULL,NULL);
INSERT INTO help_m VALUES(136,7,22,'プッシュ通知の種別','推送通知的种类',replace(replace('プッシュ通知で受取れる内容を選ぶことができます。\r\n※「LP回復」以外のプッシュ通知はスクフェスからのお知らせがある場合のみ届きます。\r\n\r\n・LP回復\r\nLPが全回復した時に通知が届きます。\r\n\r\n・アップデート情報\r\nアップデート情報が届きます。\r\n\r\n・キャンペーン\r\n特別なキャンペーン情報が届きます。\r\n\r\n・楽曲\r\n特別楽曲の情報が届きます。\r\n\r\n・ログインボーナス\r\n期間限定ログインボーナスの情報が届きます。\r\n\r\n・イベント\r\nイベントの開催情報が届きます。\r\n\r\n・勧誘・セット\r\n特待生勧誘の部員追加や新セット商品、期間限定勧誘の情報が届きます。\r\n\r\n・誕生日\r\nメンバー誕生日の情報が届きます。','\r',char(13)),'\n',char(10)),replace(replace('可以选择要在推送通知中接收的内容。\r\n※“恢复耐力”以外的推送通知只会在学园偶像祭发布公告时送达。\r\n\r\n·恢复耐力\r\n在耐力全部恢复时送达通知。\r\n\r\n·升级信息\r\n送达升级信息。\r\n\r\n·宣传活动\r\n送达特殊的活动信息。\r\n\r\n·歌曲\r\n送达特殊的歌曲信息。\r\n\r\n·登录奖励\r\n送达限时登录奖励的信息。\r\n\r\n·活动\r\n送达活动的举办信息。\r\n\r\n·招募与礼包\r\n送达优等生招募的社员追加或全新礼包商品、限时招募的信息。\r\n\r\n·生日\r\n送达成员的生日信息。','\r',char(13)),'\n',char(10)),NULL,NULL,NULL);
INSERT INTO help_m VALUES(137,7,23,'サポート','联系客服',replace(replace('ゲーム中で起きた不具合に関する報告やご意見・ご要望などのゲームに関する問い合わせをする際は、「メニュー ＞ サポート」をご確認ください。\r\nなお、頂いたご意見・ご要望についてはすべて確認致しますが、原則、返信を行っておりません。\r\nあらかじめ、ご了承ください。','\r',char(13)),'\n',char(10)),'可以反馈游戏过程中出现的问题，或对游戏的意见和看法\n也可询问与游戏有关的问题。\n请注意，虽然我方会确认所有收到的意见和建议，\n但原则上并不会进行回复。\n请多多谅解。',NULL,NULL,NULL);
INSERT INTO help_m VALUES(138,7,24,'ヒント','提示',replace(replace('ゲームを進める上で、特に大切な情報を確認できます。\r\nヒントはメインストーリーの進行に合わせて増えていきます。\r\n\r\nヒントは『その他』の『ヒント』からいつでも確認することができます。','\r',char(13)),'\n',char(10)),'可以查看游戏进行中的重要信息。\n提示会随着主线剧情的推进而不断增加。\n\n提示可以在“其他”的“提示”页面\n随时进行查阅。',NULL,NULL,NULL);
INSERT INTO help_m VALUES(139,7,25,'利用規約','使用者协议',replace(replace('本サービス利用にあたっての規則です。\r\nサービス内容、プライバシーポリシー、禁止事項、免責事項などの重要な項目が記載されております。\r\n本サービスを利用することにより、本規約に同意することになります。\r\n記載内容を必ずお読みください。','\r',char(13)),'\n',char(10)),'可以查看本服务有关的使用者协议和隐私政策。\n使用者协议包括禁止事项、免责事项等重要内容。使用本服务即意味着您同意使用者协议以及隐私政策的全部规定，请认真查看其中的内容。',NULL,NULL,NULL);
INSERT INTO help_m VALUES(140,7,26,'位置情報','位置信息',replace(replace('特定の場所で端末の位置情報を送信することでアイテムが受け取れます。\r\n「メニュー ＞ 位置情報」から受け取り可能です。\r\n\r\n※ご使用端末の位置情報設定をONにしてご利用ください。\r\n※受け取れる場所とアイテムの情報はお知らせにてご確認ください。\r\n※電波状況や対象エリア周辺の建物などの影響により、取得する位置情報に誤差が生じる可能性がございます。このため対象エリア内でもプレゼントが獲得できない場合がございますので、予めご了承ください。獲得できなかった場合はWi-Fiをオンにして再度お試しください。\r\n※位置情報の取得により現在位置をトラッキングすることはありません。','\r',char(13)),'\n',char(10)),'在特定地点发送设备的位置信息\n即可领取道具奖励\n\n*请将设备的位置信息设置更改为开启，然后再使用此功能\n*可领取道具的地点与道具信息\n请参见公告\n*发送位置信息\n并不意味着您的当前位置受到追踪',NULL,NULL,NULL);
INSERT INTO help_m VALUES(141,7,27,'購入履歴','购买记录','ラブカストーンやアイテムを購入した履歴を、確認できます。','可以查看爱心/钻石的购买记录。',NULL,NULL,NULL);
INSERT INTO help_m VALUES(143,7,29,'ログインボーナス','登录奖励',replace(replace('その月のログインボーナス一覧が確認できます。\r\nログインボーナスはその日に指定されたボーナスを獲得できます。\r\nログインした日のマスには「GET」のスタンプがつきます。\r\n\r\nその日のログインボーナスが未受取りの場合は、ここから受取ることもできます。','\r',char(13)),'\n',char(10)),'可查看当月的登录奖励一览。\n玩家可获得登录当天的奖励内容。\n已登录的天份一栏上会显示“GET”的图章。\n\n若未领取当天的登录奖励，\n玩家也可从此处领取。',NULL,NULL,NULL);
INSERT INTO help_m VALUES(145,9,1,'イベント','活动',replace(replace('期間限定で特別なライブができます。\r\n詳しくは『ホーム』のイベントバナーをタップしてイベントページから『ルール説明』を読んでください。','\r',char(13)),'\n',char(10)),replace(replace('可以限期游玩特殊演唱会。\r\n如需查阅详细内容，请点击“主页”的活动横幅进入活动页面，浏览“规则说明”。','\r',char(13)),'\n',char(10)),NULL,NULL,NULL);
INSERT INTO help_m VALUES(146,6,7,'ポイントSHOP','点数商店',replace(replace('リズミックカーニバル(※)をクリアすると手に入る「ポイント」を集めて部員やアイテムと交換できます。\r\n\r\n※リズミックカーニバルは定期的に開催されます。','\r',char(13)),'\n',char(10)),replace(replace('收集完成节奏嘉年华（※）后获得的“点数”，就能兑换社员或道具。\r\n\r\n※节奏嘉年华将会定期举办。','\r',char(13)),'\n',char(10)),NULL,NULL,NULL);
INSERT INTO help_m VALUES(148,1,31,'うでまえクラスのチャレンジ課題','演唱会段位挑战课题',replace(replace('チャレンジ課題とは、自分が到達したクラスに応じて新たに課題に挑戦できるシステムです。\r\n\r\nまた、チャレンジ課題を一定数クリアすることで新たに報酬を入手することができます。\r\n\r\n※チャレンジ課題の達成状況・内容・報酬は毎月1日0:00にリセットされます。\r\n※リセット後は、ノービスのチャレンジ課題から再挑戦できます。','\r',char(13)),'\n',char(10)),replace(replace('挑战课题指的是根据自身当前等级，向新课题发起挑战的系统。\r\n\r\n另外，完成一定数量的挑战课题，就能获得新的奖励。\r\n\r\n※挑战课题的完成状态、内容、奖励将于每月1日0:00重置。\r\n※重置后，可以从新手的挑战课题开始重新挑战。','\r',char(13)),'\n',char(10)),NULL,NULL,NULL);
INSERT INTO help_m VALUES(149,1,32,'チャレンジ課題のスキップについて','关于跳过挑战课题',replace(replace('現在、挑戦中のチャレンジ課題とうでまえクラスで自分が到達したクラスが2つ以上離れている場合挑戦中のチャレンジ課題をスキップすることができます。\r\n\r\nチャレンジ課題をスキップした場合は、対象のクラスでの報酬は獲得できません。\r\n\r\n※[スキップ]ボタンは、スキップ条件を満たしている場合のみ表示されます。','\r',char(13)),'\n',char(10)),replace(replace('如当前挑战课题与自身的水平等级相差两个段位以上，则可以跳过当前的挑战课题。\r\n\r\n如选择跳过挑战课题，将无法获得相应等级的奖励。\r\n\r\n※仅在满足跳过条件时显示[跳过]键。','\r',char(13)),'\n',char(10)),NULL,NULL,NULL);
INSERT INTO help_m VALUES(150,5,9,'勧誘スタンプ','招募印章',replace(replace('特待生勧誘や一部の限定勧誘で11連勧誘を行うと勧誘スタンプカードにスタンプが押され押された場所に応じた報酬が獲得できます。\r\n\r\n勧誘スタンプカードは勧誘ごとに設定されています。\r\n\r\n勧誘スタンプの報酬内容等の詳細は、勧誘画面の「勧誘スタンプ」からご確認いただけます。\r\n\r\n※スタンプを最後まで貯めていない状態で勧誘スタンプカードの状態をリセットすることはできません。\r\n※スタンプの個数を異なる勧誘に引き継ぐことはできません。また、同じ勧誘でも期限が終了した際にスタンプの個数を引き継ぐことはできません。\r\n※勧誘スタンプで獲得した報酬は、プレゼントボックスに付与されます。\r\n※特待生勧誘では、勧誘スタンプカードの期限に合わせて注目の部員が変更され、勧誘スタンプカードの状態もリセットされます。\r\n※部員+1枚 注目のUR1枚確定の11連限定の画像が表示されている勧誘スタンプでは、11回の勧誘とは別にピックアップブースト選択対象であるUR部員が等確率で排出される1回勧誘が行われます。','\r',char(13)),'\n',char(10)),replace(replace('在优等生招募或部分限定招募中进行11连招募，就能在印章卡盖下印章，并根据印章所盖位置获得奖励。\r\n\r\n各招募设置的招募印章卡相互独立。\r\n\r\n招募印章的奖励内容等详细情况，可以前往招募界面的“招募印章”进行查看。\r\n\r\n※无法在印章没有盖满的情况下重置招募印章卡的状态。\r\n※不同的招募无法继承印章数量。即使是同一个招募，也无法在招募结束后继承印章数量。\r\n※招募印章所获奖励将发放到礼物盒。\r\n※在优等生招募中，如根据招募印章卡的期限变更热门社员，则招募印章卡的状态也会被重置。\r\n※社员+1张 对于显示必得1张热门UR的11连限定图像的招募印章，除11连招募外，还将进行1次以同等概率出现“Pick up Boost”所选UR社员的招募。','\r',char(13)),'\n',char(10)),NULL,NULL,NULL);
INSERT INTO help_m VALUES(153,9,2,'メドレーフェスティバル','MEDLEY FESTIVAL',replace(replace('最大3曲の課題曲を連続プレイして『メドレーフェスティバル』を成功させるイベントです。\r\n条件を達成してミッションのLvを上げるほど、報酬が豪華になります。\r\n\r\n\r\n【遊び方】\r\n①遊びたい曲数を選ぶ\r\nプレイしたい曲数を選択すると課題曲が発表されます。\r\nその楽曲ごとに挑戦したい難易度を選択し、『フェス』を始めましょう！\r\nその際、『G』を使用して『アレンジ』することで、フェスの成功に有利な特殊効果をライブ中に発動できます。\r\n※2曲以上をプレイする場合、難易度選択中に表示される『ポーズアシスト』をONにすることで楽曲の間に自動で一時停止します。画面をタップすると次のライブが開始されます。\r\n※『ポーズアシスト』中にアプリを強制終了等した場合、報酬を受け取ることができません。\r\n\r\nさらに、フェス中の各楽曲プレイ開始時に友達や学院生が応援に駆けつけてくれることも！\r\n応援により様々な効果が発生します。\r\n※1回のフェスで発動した同じ効果の応援は、そのフェス中では重複して発生しません。\r\n\r\n\r\n②楽曲をプレイして『メドレーミッション』に挑戦！\r\n選んだ曲数分の楽曲を連続でプレイし、『メドレーミッション』に挑戦しましょう。\r\n2曲以上を選択した場合、各楽曲でのコンボ数と体力は次の楽曲へ引き継がれません。\r\n各楽曲で達成した最大コンボ数は『TOTAL COMBO』として合算されます。\r\n『メドレーミッション』の達成基準は以下の通りです。\r\n\r\n・コンボランク\r\n『TOTAL COMBO』の数に応じて達成されます\r\n\r\n・スコアランク\r\n各楽曲で達成したSCOREの合計に応じて達成されます\r\n\r\n・タップ評価数\r\n各楽曲で達成したタップ評価の数(例: PERFECT数)の合計に応じて達成されます\r\n\r\n※失敗するとクリア後の報酬は獲得できません。また、『メドレーミッション』の挑戦チャンスの回数が減少し、獲得可能な『イベントpt』が減少します。(獲得できないこともあります)\r\n※選択した難易度に応じて消費LPやクリア後の報酬が変動します。\r\n※本イベントではライブ設定にて特技をOFFにしている場合、特技が発動しませんのでご注意ください。\r\n\r\n\r\n③『アンコールタイム』に挑戦！\r\n選んだ曲数分の課題曲のプレイが終わると追加で1曲プレイすることができる『アンコールタイム』が発生することがあります。\r\n『アンコールタイム』の課題曲をプレイすることで、『メドレーミッション』達成に必要な合計SCOREやTOTAL COMBO、タップ評価をさらに伸ばし、有利に進めることができます。\r\n※アンコールタイムの課題曲をプレイしない場合や、ライブ失敗になった場合でも、獲得した報酬は失われません。\r\n※アンコールタイムで獲得したSCOREはごほうびBOX・スペシャルごほうびBOXのゲージ加算の対象外です。また、SCOREランキングに反映されません。\r\n\r\n\r\n④報酬をゲット！\r\nフェスを成功させると、難易度と曲数により決まる『イベントpt』を取得できます。\r\n『イベントpt』はSCOREやコンボ数、Gによるアレンジや応援の効果より、ボーナスが発生することがあります。\r\n『イベントpt』の累計によって報酬を得ることができます。\r\n\r\nまたフェスでは『金・銀・銅』の3種類の報酬を獲得できます。\r\n難易度の高いフェスを成功させると金や銀の報酬を獲得しやすくなります。\r\n\r\nさらに、メドレーミッションをクリアすると報酬を獲得できます！\r\n※詳細は【報酬獲得条件】をご覧ください。\r\n\r\n\r\n※イベント終了時刻を過ぎてからライブが終了するとイベントptは獲得できません。また、ライブ終了時にメンテナンスに入りますとEXP、G、部員なども獲得できなくなります。終了直前のライブはご注意くださいますようお願いいたします。\r\n\r\n【報酬獲得条件】\r\n・達成報酬\r\nイベントptの累計獲得数に応じて順次配布\r\n\r\n・累計ptランキング報酬\r\n期間中の累計イベントpt獲得ランキングの順位に応じて配布\r\n\r\n・SCOREランキング報酬\r\n期間中にイベントでプレイした全ての楽曲を対象とした「SCOREランキング」の順位に応じて配布\r\n\r\nSCOREランキングページでは、上位10人のハイスコアを獲得した時のユニットや楽曲を閲覧することもできます。\r\nまた、友達ランキングページでも、友達のユニットや楽曲を閲覧できます。\r\n参考にして上位を目指しましょう！\r\n\r\n※ランキング報酬を獲得できる人数は他イベントと同様で、変更はございません。\r\n※ランキング報酬の配布はイベント終了後となります。\r\n※アンコールタイムでプレイした楽曲はSCOREランキングの集計対象外です。\r\n\r\n・メドレーミッション報酬\r\n出現するメドレーミッションを達成することで獲得\r\n※初めて達成するLvのミッションでは「初回報酬」を、2回目以降の達成では「通常報酬」を獲得できます。\r\n※「アレンジ」を報酬として獲得すると、次のライブで該当アレンジが適用されます。その場合、次のライブで該当アレンジをGを消費して重複して適用することができます。\r\n\r\n【課題曲について】\r\nメドレーフェスティバルの課題曲については以下の通りです。\r\n\r\n・1プレイごとにランダムで決まります\r\n・プレイ曲数が2曲以上の場合、課題曲は全て同じ属性となります\r\n・2曲以上プレイする場合の課題曲は重複しません\r\n・課題曲の対象楽曲はスマイル・ピュア・クールの属性ごとにEASYからMASTERまで全難易度で8曲ずつとなります\r\n\r\nどの曲が配信されるか楽しみにしていてくださいね！','\r',char(13)),'\n',char(10)),replace(replace('本活动为连续游玩最多三首课题曲目，以成功举办“MEDLEY FESTIVAL”。\r\n完成条件并提升任务等级，就能使奖励变得越来越豪华。\r\n\r\n\r\n【游玩方法】\r\n①选择想要游玩的乐曲\r\n选择想要游玩的乐曲后，将公布课题曲目。\r\n选择想要挑战的各乐曲的难度，然后开始“歌谣祭”！\r\n此时，通过使用“金币”来“编舞”，就能在演出中发动有利的特殊效果，让演出更容易成功。\r\n*在游玩多首乐曲时，在难度选项中启用“辅助暂停”，乐曲就会在切换的间隙暂停。点击画面就能开始下一场演唱会。\r\n※如在“辅助暂停”期间采取强行关闭应用程序等操作，将无法获得奖励。\r\n\r\n此外，在歌谣祭各乐曲的演出开始时，好友和同学们也会赶来为你应援！\r\n应援可带来各种各样的效果。\r\n※单次歌谣祭中不会重复出现相同效果的应援。\r\n\r\n\r\n②游玩乐曲，挑战“连奏任务”！\r\n连续游玩自选的乐曲，挑战“连奏任务”吧。\r\n选择多首乐曲时，各乐曲的连击数和体力不会被继承到下首乐曲中。\r\n在各乐曲中完成的最大连击数会被计入“总连击”。\r\n“连奏任务”的完成标准如下所示。\r\n\r\n·连击级别\r\n根据“总连击”的数字判断是否完成。\r\n\r\n·分数级别\r\n根据每首歌完成的分数总和判断是否完成。\r\n\r\n·点击评价数\r\n根据每首歌获得的点击评价数量（例：PERFECT数）总和判断是否完成。\r\n\r\n※如未能完成，将无法获得通关后的奖励。同时还会减少“连奏任务”的挑战机会次数，减少可获取的“活动积分”。（甚至可能无法获得）\r\n※消耗LP和通关奖励会随着所选难度而改变。\r\n※请注意，如在演唱会设置中选择关闭特技，本活动期间将无法发动特技。\r\n\r\n\r\n③挑战“安可时间”\r\n有时会发生“安可时间”事件，玩家在完成选定的课题曲目后，还可追加游玩1首乐曲。\r\n完成“安可时间”的课题曲目能够提升“连奏任务”所要求的总分、总连击数、点击评价，让情况更加有利。\r\n※即使放弃或未能完成安可时间的课题曲目，也不会失去已经获得的奖励。\r\n※安可时间获得的分数不会累积到奖励箱和特殊奖励箱槽，也不会反馈到分数排行榜。\r\n\r\n\r\n④获取奖励！\r\n完成歌谣祭，即可根据难度和乐曲数量获得相应的“活动积分”。\r\n分数、连击数、金币所产生的打扮和应援效果可能使“活动积分”获得加成。\r\n根据累计“活动积分”，可获得最终的奖励。\r\n\r\n歌谣祭可以获得“金、银、铜”三种奖励。\r\n完成难度越高的歌谣祭，就越容易获得金、银级别的奖励。\r\n\r\n此外，完成连奏任务也可获得奖励！\r\n※详细内容请查阅【奖励获得条件】。\r\n\r\n\r\n※如结束演唱会时已经超过活动结束时间，将无法获得活动积分。此外，如结束演唱会时若处于维护期间，将无法获得经验值、金币和社员等。在临近活动结束期间游玩演唱会时，请多多注意。\r\n\r\n【奖励获得条件】\r\n·完成奖励\r\n根据活动积分的累计获得数量依次发放\r\n\r\n·累计积分排行榜奖励\r\n根据活动期间累计获得活动积分排行榜的名次依次发放\r\n\r\n·分数排行榜奖励\r\n根据活动期间游玩的所有乐曲“分数排行榜”的名次依次发放\r\n\r\n分数排行榜可以浏览前十名获得高分时的所用角色与乐曲。\r\n还可以在好友排行榜页面浏览好友的所用角色和乐曲。\r\n以此为参考，力争上游吧！\r\n\r\n※可以获得排行榜奖励的人数与其他活动一样，不作变更。\r\n※排行榜奖励将在活动结束后发放。\r\n※在安可时间内游玩的乐曲不会被计入分数排行榜。\r\n\r\n·连奏任务奖励\r\n完成出现的任务奖励后获得\r\n※完成首次出现的任务级别可以获得“首次奖励”，第二次以后完成可以获得“常规奖励”。\r\n※获得“打扮”奖励后，可以在下场演唱会中使用该打扮。此时花费金币在下场演唱会使用该打扮，可以获得二次效果。\r\n\r\n【关于课题曲目】\r\nMEDLEY FESTIVAL的课题曲目如下所示。\r\n\r\n·每次游玩时随机决定。\r\n·游玩乐曲数量超过两首时，课题曲目的属性相同。\r\n·游玩数量超过两首时，课题曲目不会重复。\r\n·课题曲目涵盖了EASY至MASTER难度，甜美、清纯、洒脱属性各八首歌。\r\n\r\n敬请期待届时发布的乐曲吧！','\r',char(13)),'\n',char(10)),NULL,NULL,NULL);
INSERT INTO help_m VALUES(154,9,3,'アイコンコレクション','音符收藏',replace(replace('イベント楽曲以外のすべての楽曲に『イベントアイコン』が登場し、これをタッチする事で『イベントアイコン』と『イベントpt』を獲得することができます。\r\n※AC譜面では獲得できません。\r\n\r\n集めた『イベントアイコン』は、『イベント楽曲』をプレイする際に必要です。\r\n『イベント楽曲』をプレイすると、通常より多くの『イベントpt』が獲得できます。\r\n※『イベント楽曲』で獲得できる『イベントpt』の量はスコアとコンボのRANKによって増減します。\r\n\r\nまた、イベントTOPページ画面右上部から、イベント楽曲に直接移動することができます。\r\n\r\n※イベント終了時刻を過ぎてからライブが終了するとイベントptは獲得できません。また、ライブ終了時にメンテナンスに入りますとEXP、G、部員なども獲得できなくなります。終了直前のライブはご注意くださいますようお願いいたします。\r\n※『イベント楽曲』は後日通常楽曲として配信予定です。\r\n※ライブ♪アリーナでは『イベントアイコン』は登場しません。\r\n\r\n【報酬獲得条件】\r\n・達成報酬\r\nイベントptの累計獲得数に応じて順次配布\r\n\r\n・累計ptランキング報酬\r\n期間中の累計イベントpt獲得ランキングの順位に応じて配布\r\n\r\n・SCOREランキング報酬\r\n期間中にイベントでプレイした全ての楽曲を対象とした「SCOREランキング」の順位に応じて配布\r\n\r\nSCOREランキングページでは、上位10人のハイスコアを獲得した時のユニットや楽曲を閲覧することもできます。\r\nまた、友達ランキングページでも、友達のユニットや楽曲を閲覧できます。\r\n参考にして上位を目指しましょう！\r\n\r\n※ランキング報酬を獲得できる人数は他イベントと同様で、変更はございません。\r\n※ランキング報酬の配布はイベント終了後となります。\r\n\r\n【配信楽曲について】\r\n配信楽曲の難易度は以下の通りです。\r\n\r\nEASY（必要イベントアイコン数：15）\r\nNORMAL（必要イベントアイコン数：30）\r\nHARD（必要イベントアイコン数：45）\r\nEXPERT（必要イベントアイコン数：75）\r\nランダムEXPERT（必要イベントアイコン数：75）\r\nMASTER（必要イベントアイコン数：75）\r\n\r\n※ランダム譜面はEXPERT楽曲のみの配信になります。\r\n※SCOREランキングの対象は全難易度です。\r\n※ランダム譜面のリズムアイコンが落ちてくる位置は全てランダムです。\r\n※イベント楽曲以外の楽曲で、楽曲選択画面右下の消費LP変更機能を使用することで、通常の報酬に加え獲得出来る『イベントアイコン』の量が変化します。さらに、イベント楽曲では消費する『イベントアイコン』の数を変更することで、通常の報酬に加え獲得出来るイベントptの量が変化します。\r\n\r\n【キャンペーンについて】\r\nイベント終了日3日前の0時から獲得Gが3倍に増加します。\r\n※期間中、通常楽曲・特別楽曲・MASTERチャレンジで配信されている楽曲が対象です。\r\n※イベント楽曲は対象外です。\r\n※AC譜面は本キャンペーンの対象外となります。','\r',char(13)),'\n',char(10)),replace(replace('活动乐曲以外的所有乐曲都将出现“活动音符”，点击后可以获得“活动音符”与“活动积分”。\r\n※AC谱面无法获得。\r\n\r\n积攒的“活动音符”将用于游玩“活动乐曲”。\r\n游玩“活动乐曲”可以获得更多的“活动积分”。\r\n※“活动乐曲”中可以获得的“活动积分”数量将随分数和连击等级而改变。\r\n\r\n还可以直接从活动首页右上方跳转到活动乐曲。\r\n\r\n※如结束演唱会时已经超过活动结束时间，将无法获得活动积分。此外，如结束演唱会时若处于维护期间，将无法获得经验值、金币和社员等。在临近活动结束期间游玩演唱会时，请多多注意。\r\n※“活动乐曲”将在随后作为常规乐曲发布。\r\n※演唱会♪竞技场中不会出现“活动音符”。\r\n\r\n【奖励获得条件】\r\n·达成奖励\r\n根据活动积分的累计获得数量依次发放\r\n\r\n·累计积分排行榜奖励\r\n根据活动期间累计获得活动积分排行榜的名次依次发放\r\n\r\n·分数排行榜奖励\r\n根据活动期间游玩的所有乐曲“分数排行榜”的名次依次发放\r\n\r\n分数排行榜可以浏览前十名获得高分时的队伍与乐曲。\r\n还可以在好友排行榜页面浏览好友的所用角色和乐曲。\r\n以此为参考，力争上游吧！\r\n\r\n※可以获得排行榜奖励的人数与其他活动一样，不作变更。\r\n※排名奖励将在活动结束后发放。\r\n\r\n【关于发布乐曲】\r\n发布乐曲的难度如下所示。\r\n\r\nEASY（所需活动音符数量：15）\r\nNORMAL（所需活动音符数量：30）\r\nHARD（所需活动音符数量：45）\r\nEXPERT（所需活动音符数量：75）\r\n随机EXPERT（所需活动音符数量：75）\r\nMASTER（所需活动音符数量：75）\r\n\r\n※只有EXPERT乐曲会发布随机谱面。\r\n※所有难度都将列入分数排行榜。\r\n※随机谱面的节奏音符掉落位置完全随机。\r\n※游玩活动乐曲以外的乐曲时，使用选歌界面右下方的变更消耗LP功能，就能使常规奖励和获得的“活动音符”数量发生变化。此外，变更活动乐曲所消耗的“活动音符”数量，就能使常规奖励和活动积分发生变化。\r\n\r\n【关于冲刺活动】\r\n活动结束三天前的零点起，可以获得的金币数量将变为三倍。\r\n※包括活动期间，在常规乐曲、特殊乐曲、MASTER挑战中发布的乐曲。\r\n※不包括活动乐曲。\r\n※本冲刺活动不包括AC谱面。','\r',char(13)),'\n',char(10)),NULL,NULL,NULL);
INSERT INTO help_m VALUES(155,9,4,'なかよしマッチ','友情大合战',replace(replace('4人で協力してミッションをクリアするイベントです。\r\n\r\n【遊び方】\r\n①イベントTOPの「イベントに参加する」を選択すると、全国のプレイヤーと協力ライブを行います。\r\n楽曲と楽曲ミッションが発表されます。\r\n「プライベート」をタップすると、特定のプレイヤーと協力して遊ぶこともできます。\r\n詳しくは【プライベートマッチについて】をご覧ください。\r\n難易度を選択すると、挑戦する楽曲と「楽曲ミッション」が発表されます。\r\n\r\n②楽曲に挑戦するための「ユニット」を選択します。\r\nユニットの「おたすけパワー」が多いほど「楽曲ミッション」が達成しやすくなります。\r\n\r\n③メンバーが4人マッチングしたらライブ開始！\r\n4人のおたすけパワーの合計で、発生するボーナスの効果量がアップします！\r\n\r\n④ライブが成功すると、「楽曲ミッション」の達成度と個人の貢献度が発表されます。\r\n楽曲ミッションの達成度や貢献度が高いほど、イベントptを多くゲットできます。\r\n楽曲ミッションの目標を大きく超えると、より豪華な報酬を獲得するチャンスも！\r\n\r\n⑤イベント期間中、「みんなの目標」が設定されます。\r\nイベントに参加した全学院生のライブ結果の合計に応じて「みんなの目標」の達成報酬を獲得できます。\r\n全国の学院生と力を合わせて「みんなの目標」を達成しましょう！\r\n※詳しくは【報酬獲得条件】の「みんなの目標」をご覧ください。\r\n\r\nマッチング時や結果発表時には、μ''sメンバーやAqoursメンバー、定型文のスタンプを使ってコミュニケーションを取ることもできます。\r\nイベントTOPからスタンプカスタマイズ画面に遷移することができますので、この機会にぜひお試しください！\r\n\r\n※マッチング時に人数が不足している場合はCPUがマッチングされます。\r\n※同一難易度を連続プレイする場合、同じ楽曲は連続で抽選されにくくなっております。\r\n※『なかよしマッチ』でプレイできる楽曲はイベントごとに決まった課題曲の中からランダムで選曲されます。詳しくは【対象楽曲について】をご覧ください。\r\n\r\n※イベント終了時刻を過ぎてからライブが終了するとイベントptは獲得できません。また、ライブ終了時にメンテナンスに入りますとEXP、G、部員なども獲得できなくなります。終了直前のライブはご注意くださいますようお願いいたします。\r\n※難易度選択ボタンを押すことでLPが消費されます。難易度選択後、ライブ終了前にアプリを強制終了等した場合は、LPは消費したままとなります。プライベートマッチの場合はOKを押してライブ開始後にLPが消費されます。\r\n\r\n・ライブに失敗したり、ライブ終了までに一定時間を要した場合には、楽曲ミッションの達成度はプレイヤーの個人成績のみを基に発表されます。また、その達成度にかかわらず貢献度は最下位となります。その際のイベントpt、楽曲ミッション報酬は発表された達成度に応じて処理されます。\r\n・なかよしマッチではライブ失敗時にコンティニューができません。高難易度をプレイされる際にはご注意ください。\r\n・『なかよしマッチ』での楽曲クリア数もライブ課題の達成数にカウントされます。\r\n・『なかよしマッチ』のライブも『ファーストライブボーナス』の受け取り対象となります。\r\n\r\n【報酬獲得条件】\r\n・達成報酬\r\nイベントptの累計獲得数に応じて順次配布\r\n\r\n・累計ptランキング報酬\r\n期間中の累計イベントpt獲得ランキングの順位に応じて配布\r\n\r\n・SCOREランキング報酬\r\n期間中にイベントでプレイした全ての楽曲を対象とした「SCOREランキング」の順位に応じて配布\r\n\r\nSCOREランキングページでは、上位10人のハイスコアを獲得した時のユニットや楽曲を閲覧することもできます。\r\nまた、友達ランキングページでも、友達のユニットや楽曲を閲覧できます。\r\n参考にして上位を目指しましょう！\r\n\r\n※ランキング報酬を獲得できる人数は他イベントと同様で、変更はございません。\r\n※ランキング報酬の配布はイベント終了後となります。\r\n\r\n・みんなの目標報酬\r\nイベントに参加した全学院生のライブ結果の合計に応じて獲得\r\n※『なかよしマッチ』以外のライブは「みんなの目標」の集計対象外です。\r\n※「みんなの目標」の達成報酬は、目標を一つクリアすることでプレゼントボックスに送られます。\r\n※参加していない目標の達成報酬は受け取ることができません。\r\n\r\n・楽曲ミッション達成報酬\r\nライブ参加者4人のライブ結果を基に決まるミッション達成度に応じて獲得\r\n※ライブに失敗した参加者は受け取れません。\r\n\r\n【対象楽曲について】\r\nなかよしマッチの対象楽曲については以下の通りです。\r\n・EASY/NORMAL/HARD/EXPERT/MASTER\r\n各18曲（各属性6曲ずつ）がランダムで出現\r\n\r\n※『消費LP変更機能』を使用することで、通常の報酬[G        EXPなど]に加え、獲得できるイベントptの量と「みんなの目標」に反映されるスコアが変化します。\r\n※『消費LP変更機能』を使用した場合でも「スコアの楽曲ミッション」のカウントに変化はありません。\r\n※『消費LP変更機能』を使用したライブに失敗した時に得られるイベントptに変化はありません。\r\n\r\n【おたすけパワーについて】\r\n協力ライブで使用するユニットに付与される「おたすけパワー」が多いほど楽曲ミッションを達成しやすくなります。\r\nレアリティの高い部員を育成したり部員の特技Lvを上げることでおたすけパワーを増やすことができます。\r\n※特典やログインボーナス、一部の部員交換チケット『キラキラ☆転入生フェスティバル』等で配信した部員はR部員と同じパワーに換算されます。\r\n\r\n部員を育成してイベントを有利に進めましょう！\r\n\r\n\r\n【プライベートマッチについて】\r\nIDを使って仲間と協力ライブ(プライベートマッチ)がプレイできます。\r\n\r\n[プライベートマッチの参加者を募集する場合]\r\n・イベントTOP右下の「プライベート」をタップします\r\n・「参加者を募集する」タブで遊びたい難易度を選択し、「募集」します\r\n・表示される6桁のIDを一緒に遊びたい仲間に教えて入力してもらいましょう！\r\n・4人揃うと自動でライブが開始されます\r\n・また2人以上マッチングしていれば「OK」ボタンを押すことでライブを開始できます空いた枠には、CPUがマッチングされます\r\n\r\n[プライベートマッチに参加する場合]\r\n・イベントTOP右下の「プライベート」をタップします\r\n・「IDを入力して参加する」タブで6桁のIDを入力し「参加」します\r\n・メンバーが4人揃うと自動でライブが開始されます\r\n・メンバーが3人以下の場合、募集者が「OK」ボタンを押すとライブが開始されます\r\n\r\n※ライブに必要なLPが足りない場合、参加者を募集できません。\r\n※各種報酬はプライベートマッチでも変わりません。\r\n※プライベートマッチの募集、参加を取りやめる場合は「Back」ボタンをタップしてください。','\r',char(13)),'\n',char(10)),replace(replace('四人合作完成任务的活动。\r\n\r\n【游玩方法】\r\n①点击活动首页的“参加活动”，就能与全国玩家进行合作演唱会。\r\n同时发布乐曲和乐曲任务。\r\n点击“私人游玩”，可以与特定玩家合作游玩。\r\n详细内容请查阅【关于私人匹配】\r\n选择难度后，将发布需要挑战的乐曲和“乐曲任务”。\r\n\r\n②选择用于挑战乐曲的“角色”。\r\n角色的“助力”越高，“乐曲任务”就越容易完成。\r\n\r\n③四名玩家匹配后，将开始演唱会！\r\n四人的助力总和将提升加成效果！\r\n\r\n④演唱会成功后，将发布“乐曲任务”的完成度和个人贡献度。\r\n乐曲任务的完成度及贡献度越高，能够获得的活动积分就越多。\r\n如果能够大幅度超出乐曲任务的目标，说不定就有机会获得豪华奖励！\r\n\r\n⑤活动期间，将设定“全员目标”。\r\n参加活动的全体同学演唱会成绩累积到一定程度，就能获得“全员目标”的累计奖励。\r\n与全国同学一起合作完成“全员目标”吧！\r\n※详细内容请查阅【奖励获得条件】中的“全员目标”。\r\n\r\n进行匹配及公布成绩时，可以使用μ''s社员、Aqours社员和固定内容的图章进行简单交流。\r\n从活动首页可以跳转到图章编辑界面，欢迎借此机会尝试一下！\r\n\r\n※如匹配人数不足，将匹配CPU。\r\n※连续游玩同一难度，连续抽到同一首歌的概率将会降低。\r\n※可以在“友情大合战”中游玩的乐曲将从各活动选定的课题曲目中随机抽选。详细内容请查阅【关于对象乐曲】\r\n\r\n※如结束演唱会时已经超过活动结束时间，将无法获得活动积分。此外，如结束演唱会时若处于维护期间，将无法获得经验值、金币和社员等。在临近活动结束期间游玩演唱会时，请多多注意。\r\n※点击选择难度键将消耗LP。如在选择难度后或演唱会结束前对游戏进行强制关闭等操作，已消耗的LP将无法退还。游玩私人匹配时，将在点击OK并开始演唱会后消耗LP。\r\n\r\n·如演唱会失败或距离演唱会结束还需等待一段时间，将仅根据玩家的个人成绩公布乐曲任务的完成度。此外，无关完成度的多少，贡献度都将变成最后一名。届时，将根据公布的完成度处理活动积分和乐曲任务奖励。\r\n·友情大合战无法在演唱会失败时接关。游玩高难度乐曲时请多多注意。\r\n·“友情大合战”的乐曲通关数量将计入演唱会课题的完成数量。\r\n·“友情大合战”的演唱会属于“首场演唱会奖励”领取对象。\r\n\r\n【奖励获得条件】\r\n·完成奖励\r\n根据活动积分的累计获得数量依次发放\r\n\r\n·累计积分排行榜奖励\r\n根据活动期间累计获得活动积分排行榜的名次依次发放\r\n\r\n·分数排行榜奖励\r\n根据活动期间游玩的所有乐曲“分数排行榜”的名次依次发放\r\n\r\n分数排行榜可以浏览前十名获得高分时的所用角色与乐曲。\r\n还可以在好友排行榜页面浏览好友的所用角色和乐曲。\r\n以此为参考，力争上游吧！\r\n\r\n※可以获得排行榜奖励的人数与其他活动一样，不作变更。\r\n※排名奖励将在活动结束后发放。\r\n\r\n·全员目标奖励\r\n全国同学一起累计奖励参加活动的全体同学演唱会成绩累积到一定程度，就能获得“全员目标”的。\r\n※“友情大合战”以外的演唱会不属于“全员目标”统计对象。\r\n※每完成一个“全员目标”，奖励都将被送入礼物箱。\r\n※无法领取未参加的完成目标奖励。\r\n\r\n·乐曲任务完成奖励\r\n根据四名演唱会参加者成绩所决定的任务完成度发放奖励\r\n※演唱会失败的参加者无法获得奖励。\r\n\r\n【关于对象乐曲】\r\n友情大合战的对象乐曲如下所示。\r\n·EASY/NORMAL/HARD/EXPERT/MASTER\r\n各18首歌（每个属性各6首）随机出现\r\n\r\n※使用“变更LP功能”，就能使常规奖励（金币、经验值等）、活动积分、计入到“全员目标”的分数发生变化。\r\n※使用“变更消耗LP功能”并不影响“分数的乐曲任务”计数。\r\n※如在使用“变更消耗LP功能”时演唱会失败，所获活动分数将没有变化。\r\n\r\n【关于助力】\r\n在合作演唱会中，所使用的角色“助力”越多，乐曲任务就越容易完成。\r\n培养稀有度高的社员、提高社员的特技等级都能增加助力。\r\n※特典、登录奖励、部分社员兑换券“亮晶晶☆转校生歌谣祭”等活动中发布的社员将换算为与R社员相同的助力。\r\n\r\n努力培养社员，让活动玩起来更轻松吧！\r\n\r\n\r\n【关于私人匹配】\r\n可以使用ID与朋友一起游玩合作演唱会（私人匹配）。\r\n\r\n[如需招募私人匹配的参加者]\r\n·点击活动首页右下方的“私人匹配”。\r\n·在“招募参加者”标签中选择难度，并点击“招募”。\r\n·将显示的六位ID告诉准备一起游玩的朋友，让朋友输入ID！\r\n·集齐四人后将自动开始演唱会。\r\n·匹配到两名以上玩家后，点击“OK”键就能开始演唱会，空位将自动匹配CPU。\r\n\r\n[如需参加私人匹配]\r\n·点击活动首页右下方的“私人匹配”。\r\n·在“输入ID参加匹配”中输入六位ID，然后点击“参加”。\r\n·集齐四人后将自动开始演唱会。\r\n·玩家人数少于三人时，将在招募者点击“OK”键后开始演唱会。\r\n\r\n※如演唱会所需LP不足，将无法招募参加者。\r\n※私人匹配的各种奖励没有变化。\r\n※如需取消招募或参加私人匹配，请点击“Back”键。','\r',char(13)),'\n',char(10)),NULL,NULL,NULL);
INSERT INTO help_m VALUES(156,9,5,'チャレンジフェスティバル','Challenge Festival',replace(replace('最大5曲の課題曲を連続プレイして『チャレンジフェスティバル』を成功させるイベントです。\r\n\r\n【遊び方】\r\n①プレイしたい「コース」を選択すると楽曲が発表されます。\r\n楽曲はRoundごとに発表され、Round5まであります。\r\nRoundが進むごとに、楽曲が難しくなっていきます。\r\n\r\n※曲の途中でライブに失敗すると獲得可能な『イベントpt・G・EXP』が半減します。(1曲目の場合は獲得できません)また、そのRoundまでに出現した『金・銀・銅』の報酬は全て獲得できません。\r\n※2曲目以降の楽曲が難しい場合は「受け取って終了」を選ぶことで、そこまでに得た報酬を受け取って終了することもできます。\r\n\r\n■ポイント\r\nRoundの途中でフェスを中断することができます。\r\n直前までに発生した報酬・課題曲・ボーナス効果はプレイ再開時まで保存されます。\r\n\r\n②楽曲発表後、ボーナスが発生することがあります。\r\nボーナスは最大3つ発生し、組み合わせによっては条件付きボーナスが発生することもあります。\r\n※ボーナスの効果は発生したRoundに対してのみ有効です。\r\n\r\n■ポイント\r\nボーナスはRoundが進むほど発生しやすくなります。\r\n\r\n③使用するユニットを選択します。\r\nユニットはRoundごとに選択することができます。\r\n\r\n④『G』を使用して『アレンジ』することで、特殊効果をライブ中に発動できます。\r\nアレンジの効果はそのRoundの間のみ有効です。\r\n\r\n⑤ライブをクリアすると、Roundごとにイベントpt・G・EXP・『金・銀・銅』の報酬が出現します。\r\nRoundを進めるほど、イベントpt・G・EXPの獲得量が増え、『金・銀』の報酬を獲得しやすくなります。\r\nまた、『金・銀・銅』の報酬[レアリティN及びRの部員]の初期Lvは、選択したコースに応じた値になります。\r\n\r\n※各Round終了時に出現する報酬は、Round5をクリア、もしくは「受け取って終了」を選択するまで獲得できません。\r\n\r\n⑥『イベントpt』の累計によってイベント報酬を獲得することができます。\r\nまた、イベント終了時のランキングによって獲得できるランキング報酬もあります。\r\n\r\n※イベント終了時刻を過ぎてからライブが終了するとイベントptは獲得できません。また、ライブ終了時にメンテナンスに入りますとEXP、G、部員なども獲得できなくなります。終了直前のライブはご注意くださいますようお願いいたします。\r\n\r\n【報酬獲得条件】\r\n・達成報酬\r\nイベントptの累計獲得数に応じて順次配布\r\n\r\n・累計ptランキング報酬\r\n期間中の累計イベントpt獲得ランキングの順位に応じて配布\r\n\r\n・SCOREランキング報酬\r\n期間中にイベントでプレイした全ての楽曲を対象とした「SCOREランキング」の順位に応じて配布\r\n\r\nSCOREランキングページでは、上位10人のハイスコアを獲得した時のユニットや楽曲を閲覧することもできます。\r\nまた、友達ランキングページでも、友達のユニットや楽曲を閲覧できます。\r\n参考にして上位を目指しましょう！\r\n\r\n※ランキング報酬を獲得できる人数は他イベントと同様で、変更はございません。\r\n※ランキング報酬の配布はイベント終了後となります。\r\n\r\n【課題曲について】\r\nチャレンジフェスティバルの課題曲については以下の通りです。\r\n\r\n・各コース、1Roundあたり8曲の中から抽選され、1Round1曲ずつ、最大5Round(5曲)までプレイできます\r\n・Roundが進むにつれて、ステージレベル(★の数)が多い楽曲が出現しやすくなります\r\n・コースとRoundごとに出現する楽曲は異なります(一部同じ場合もあります)\r\n・各コースに出現する楽曲の難易度とステージレベルは以下の通りです\r\n初級：★1〜★6（EASYとNORMAL）\r\n中級：★4〜★8（NORMALとHARD）\r\n上級：★6〜★10（HARDとEXPERT）\r\n超級：★9〜★11\r\n（ランダム楽曲や日替わり楽曲を含むEXPERT）\r\n神級：★10〜★11もしくは★10〜★12\r\n（ランダム楽曲や日替わり楽曲を含むEXPERT・5ユニットアイコン譜面・MASTER・MASTERスイング）\r\n極級：★11〜★12\r\n（5ユニットアイコン譜面・MASTER・MASTERスイング）\r\n\r\n※『消費LP変更機能』を使用することで、通常の報酬[G,EXPなど]に加え、獲得できる『イベントpt』の量が変化します。\r\n※『フェス』の楽曲クリア数もライブ課題の達成数にカウントされます。カウントは各Round終了後に行われます。\r\n※課題の達成率のカウントは、新規に課題が発生してから開始されます。ライブ課題(クリア回数・FULL COMBO数)の達成率を上回る回数を『フェス』でクリアした場合、上回った分のカウントは次の課題の達成率には引き継がれませんのでご注意ください。\r\n※『フェス』のライブも『ファーストライブボーナス』の受け取り対象となります。\r\n※ごほうびBOXのゲージは各Round終了時点で加算されます。','\r',char(13)),'\n',char(10)),replace(replace('本活动为连续游玩最多五首课题曲目，以成功举办“Challenge Festival”。\r\n\r\n【游玩方法】\r\n①选择想要游玩的“篇章”后，将公布乐曲。\r\n每回合公布乐曲，一共有五个回合。\r\n乐曲难度会随着回合的推进而增加。\r\n\r\n※如游玩途中演唱会失败，可以获得的“活动积分、金币、经验值”将减半。（如为第一首歌，将没有奖励）同时，将失去此前获得的所有“金、银、铜”奖励。\r\n※如认为第二首之后的乐曲难度较大，可以选择“领取奖励后结束游玩”，以此拿到奖励后结束本次游玩。\r\n\r\n■重点\r\n可以在回合期间中断歌谣祭。\r\n此前出现的奖励、课题曲目、加成效果将保留到继续游玩时。\r\n\r\n②公布乐曲后，将出现加成。\r\n最多可以出现三个加成，同时存在因组合方式出现的附带条件加成。\r\n※加成仅在该加成出现的回合有效。\r\n\r\n■重点\r\n加成的出现率将随着回合推进而不断提升。\r\n\r\n③选择使用的角色。\r\n各回合均可选择角色。\r\n\r\n④使用“金币”进行“打扮”，可以在演唱会期间发动特殊效果。\r\n打扮仅在该回合有效。\r\n\r\n⑤各回合完成演唱会后，都会出现活动积分、金币、经验值和“金、银、铜”奖励。\r\n随着回合的推进，活动积分、金币、经验值的数量及“金、银”奖励出现率将不断增加。\r\n作为“金、银、铜”奖励的[稀有度N及R的社员]初期等级将随所选篇章而定。\r\n\r\n※完成第五回合或选择“领取奖励后结束游玩”后，才能获得各回合结束后出现的奖励。\r\n\r\n⑥“活动积分”累积到一定数值，就能获得活动奖励。\r\n还有根据活动结束时的排行榜发放的排名奖励。\r\n\r\n※如结束演唱会时已经超过活动结束时间，将无法获得活动积分。此外，如结束演唱会时若处于维护期间，将无法获得经验值、金币和社员等。在临近活动结束期间游玩演唱会时，请多多注意。\r\n\r\n【奖励获得条件】\r\n·完成奖励\r\n根据活动积分的累计获得数量依次发放\r\n\r\n·累计积分排行榜奖励\r\n根据活动期间累计获得活动积分排行榜的名次依次发放\r\n\r\n·分数排行榜奖励\r\n根据活动期间游玩的所有乐曲“分数排行榜”的名次依次发放\r\n\r\n分数排行榜可以浏览前十名获得高分时的所用角色与乐曲。\r\n还可以在好友排行榜页面浏览好友的所用角色和乐曲。\r\n以此为参考，力争上游吧！\r\n\r\n※可以获得排行榜奖励的人数与其他活动一样，不作变更。\r\n※排名奖励将在活动结束后发放。\r\n\r\n【关于课题曲目】\r\nChallenge Festival的课题曲目如下所示。\r\n\r\n·各篇章的每个回合，都将从八首歌中进行抽选，每回合一首，最多可以游玩五个回合（五首歌）。\r\n·随着回合的推进，舞台等级（★的数量）更多的乐曲出现率将越来越高。\r\n·各篇章及章节出现的乐曲不同（部分重复）。\r\n·各篇章出现的乐曲难度和舞台等级如下所示。\r\n初级：★1-★6（EASY和NORMAL）\r\n中级：★4-★8（NORMAL和HARD）\r\n上级：★6-★10（HARD和EXPERT）\r\n超级：★9-★11\r\n（包含随机乐曲和日替乐曲的EXPERT）\r\n神级：★10-★11或★10-★12\r\n（包含随机乐曲和日替乐曲的EXPERT、五角色图标谱面、MASTER、MASTER滑键谱面）\r\n极级：★11-★12\r\n（五角色图标谱面、MASTER、MASTER滑键谱面）\r\n\r\n※使用“变更LP功能”，就能使常规奖励（金币、经验值等）和可以获得的活动积分发生变化。\r\n※“歌谣祭”的乐曲通关数量计入课题中的完成数量。各回合结束后进行计数。\r\n※课题完成率将在发生新课题后开始计数。请注意，如已经完成的歌谣祭次数超出了演唱会课题（完成次数、FULLCOMBO数量）的完成率，超出部分无法计入下一课题的完成率。\r\n※“歌谣祭”的演唱会属于“首场演唱会奖励”领取对象。\r\n※奖励箱槽将在各回合结束后进行累积。','\r',char(13)),'\n',char(10)),NULL,NULL,NULL);
INSERT INTO help_m VALUES(157,9,6,'SCORE MATCH','SCORE MATCH',replace(replace('4人で対戦するタイプのイベントです。\r\n\r\n【遊び方】\r\n①難易度を選択してエントリーすると自動的に最大4人のプレイヤーとのマッチングが行われます。\r\n\r\n②ランダムで楽曲が決定し、同時にライブスタート！\r\n\r\n③終了後、獲得スコアによって順位を決定します。\r\n順位とスコアランクと難易度によって決まる『イベントpt』を獲得でき、その累計『イベントpt』によって報酬を得ることができます。\r\n\r\n④順位や難易度によって加点・減点される『スコアマッチpt』は、『スコアマッチpt』ランキング上位の相手とマッチングした場合、勝てばptを多く獲得できます。\r\n相手が下位の場合、勝って獲得できるptは少なく、順位によっては減点される可能性もあります。\r\nまた、高難易度の楽曲ほど減点されるptと加点されるptは多くなります。\r\n\r\nマッチング時や結果発表時には、μ''sメンバーやAqoursメンバー、定型文のスタンプを使ってコミュニケーションを取ることもできます。\r\nイベントTOPからスタンプカスタマイズ画面に遷移することができますので、この機会にぜひお試しください！\r\n\r\n※マッチング時に人数が不足している場合はCPUがマッチングされます。\r\n※同一難易度を連続プレイする場合、同じ楽曲は連続で抽選されにくくなっております。\r\n※イベント終了時刻を過ぎてからライブが終了するとイベントptは獲得できません。また、ライブ終了時にメンテナンスに入りますとEXP、G、部員なども獲得できなくなります。終了直前のライブはご注意くださいますようお願いいたします。\r\n※難易度選択ボタンを押すことでLPが消費されます。難易度選択後、ライブ終了前にアプリを強制終了等した場合は、LPは消費したままとなります。\r\n※SCORE MATCHではライブ失敗時にコンティニューができません。高難易度をプレイされる際にはご注意ください。\r\n※ライブに失敗したり、ライブ終了までに一定時間以上を要した場合には、獲得したSCOREにかかわらず順位は最下位となります。その際の『イベントpt』、『スコアマッチpt』は順位に応じて処理されます。\r\n\r\n【TECHNICALモードとは？】\r\n・EXPERT楽曲の中でも難易度が高い楽曲が出現する、上級者向けのモードです。プレイされる際はご注意ください。\r\n・TECHNICALモードの消費LPおよびクリア報酬は難易度EXPERTを選択した場合と同様です。\r\n\r\n【報酬獲得条件】\r\n・達成報酬\r\nイベントptの累計獲得数に応じて順次配布\r\n\r\n・累計ptランキング報酬\r\n期間中の累計イベントpt獲得ランキングの順位に応じて配布\r\n\r\n・SCOREランキング報酬\r\n期間中にイベントでプレイした全ての楽曲を対象とした「SCOREランキング」の順位に応じて配布\r\n\r\nSCOREランキングページでは、上位10人のハイスコアを獲得した時のユニットや楽曲を閲覧することもできます。\r\nまた、友達ランキングページでも、友達のユニットや楽曲を閲覧できます。\r\n参考にして上位を目指しましょう！\r\n\r\n※ランキング報酬を獲得できる人数は他イベントと同様で、変更はございません。\r\n※ランキング報酬の配布はイベント終了後となります。\r\n\r\n【対象楽曲について】\r\nTECHNICALモードでは下記の楽曲を配信いたします。\r\n・ランダムEXPERT楽曲(ステージレベル：★9以上)\r\n・一部の日替わりEXPERT楽曲(ステージレベル：★11)\r\n\r\n※ランダムEXPERT楽曲のリズムアイコンが落ちてくる位置は全てランダムです。ただし、マッチングされた4人のプレイヤーがプレイする際のリズムアイコンの位置は全て同一となります。\r\n※ランダムEXPERT楽曲は、難易度EXPERTで配信するものと同じ楽曲を配信いたします。\r\n※ランダムEXPERT楽曲の中には、初めてランダム楽曲として配信する楽曲も含まれています。','\r',char(13)),'\n',char(10)),replace(replace('四人对决型活动。\r\n\r\n【游玩方法】\r\n①选择难度进行报名，最多四名玩家将自动进行匹配。\r\n\r\n②随机选取乐曲后，将同时开始演唱会！\r\n\r\n③结束后，根据分数决定名次。\r\n根据名次、分数等级、难度可以获得“活动积分”，活动积分累积到一定程度就能获得奖励。\r\n\r\n④“SCORE MATCH积分”将根据名次和难度而变，匹配并战胜排名更高的玩家，就能获得更多积分。\r\n如对方的名次低于自己，不仅获得的积分会减少，更有可能因为名次而使积分变为负值。\r\n乐曲的难度越高，积分的加减数值越大。\r\n\r\n进行匹配及公布成绩时，可以使用μ''s社员、Aqours社员和固定内容的图章进行简单交流。\r\n从活动首页可以跳转到图章编辑界面，欢迎借此机会尝试一下！\r\n\r\n※如匹配人数不足，将匹配CPU。\r\n※连续游玩同一难度，连续抽到同一首歌的概率将会降低。\r\n※如结束演唱会时已经超过活动结束时间，将无法获得活动积分。此外，如结束演唱会时若处于维护期间，将无法获得经验值、金币和社员等。在临近活动结束期间游玩演唱会时，请多多注意。\r\n※点击选择难度键将消耗LP。如在选择难度后或演唱会结束前对游戏进行强制关闭等操作，已消耗的LP将无法退还。\r\n※SCORE MATCH无法在演唱会失败时接关。游玩高难度乐曲时请多多注意。\r\n※如演唱会失败或距离演唱会结束还需等待一段时间，无论获得多少分，都将变成最后一名。届时，将根据名次处理活动积分和分数对决积分。\r\n\r\n【什么是TECHNICAL模式？】\r\n·EXPERT乐曲之中的高难度乐曲所推出的面向熟练玩家的模式。游玩时请多多注意。\r\n·TECHNICAL模式的消耗LP和通关奖励与EXPERT难度相同。\r\n\r\n【奖励获得条件】\r\n·完成奖励\r\n根据活动积分的累计获得数量依次发放\r\n\r\n·累计积分排行榜奖励\r\n根据活动期间累计获得活动积分排行榜的名次依次发放\r\n\r\n·分数排行榜奖励\r\n根据活动期间游玩的所有乐曲“分数排行榜”的名次依次发放\r\n\r\n分数排行榜可以浏览前十名获得高分时的所用角色与乐曲。\r\n还可以在好友排行榜页面浏览好友的所用角色和乐曲。\r\n以此为参考，力争上游吧！\r\n\r\n※可以获得排行榜奖励的人数与其他活动一样，不作变更。\r\n※排名奖励将在活动结束后发放。\r\n\r\n【关于对象歌曲】\r\nTECHNICAL模式发布歌曲如下。\r\n·随机EXPERT乐曲（舞台等级：★9以上）\r\n·部分日替EXPERT乐曲（舞台等级：★11）\r\n\r\n※随机EXPERT歌曲的节奏音符掉落位置完全随机。但匹配到的四名玩家游玩时，节奏音符将掉落到相同位置。\r\n·随机EXPERT难度发布的歌曲包括在随机EXPERT歌曲内。\r\n※随机EXPERT中包括首次作为随机歌曲发布的曲目。','\r',char(13)),'\n',char(10)),NULL,NULL,NULL);
INSERT INTO help_m VALUES(158,9,9,'イベントストーリー','活动剧情',replace(replace('イベントで閲覧できるストーリーです。\r\nイベント開催期間中にイベントTOPに移動すると、プロローグを閲覧することができます。\r\n\r\n続きのストーリーは達成報酬の条件を満たすと獲得できます。\r\nストーリーは5話まで獲得できますが、エピローグはイベント期間終了後にログインすると閲覧することができます。\r\n\r\n※解放済のイベントストーリーについては、イベントページの「ストーリー」ボタンや各モードの「その他のストーリー」から閲覧することができます。\r\n※イベント開催期間を過ぎるとプロローグを獲得することができなくなります。\r\n※ストーリーを5話まで獲得していなくても、プロローグを獲得している場合はエピローグを閲覧することができます。\r\n※プロローグを獲得していても、イベント終了から次回イベント開始までにログインしなかった場合、エピローグを獲得することはできなくなります。\r\n※上記期間中に獲得できなかったストーリーは、翌月末のアップデート後に「イベントストーリー解放キー」を使用することで獲得できるようになります。','\r',char(13)),'\n',char(10)),replace(replace('可以在活动期间观看的剧情。\r\n在活动进行期间打开活动主页，即可浏览序章。\r\n\r\n之后的剧情会随着奖励条件的完成而解锁。\r\n剧情共分为5话。在活动结束后，只要登录上线即可浏览终章。\r\n\r\n※已解锁的活动剧情可以通过点击活动页面的“剧情”键或各模式的“其他剧情”浏览。\r\n※超过活动时间将无法获得序章。\r\n※届时即使没有解锁全部5话剧情，只要解锁了序章，就可观看尾声。\r\n※即使已经获得本活动剧情，如果没有在活动结束至下次活动开始期间登入游戏，将无法获得尾声。\r\n※可以在下月底版本升级后使用“活动剧情解锁钥匙”解锁未能在上述时间内获得的剧情。','\r',char(13)),'\n',char(10)),NULL,NULL,NULL);
INSERT INTO help_m VALUES(159,9,10,'イベント配信の部員について','关于活动发放的社员',replace(replace('イベントで各種報酬として初登場するシークレットSSR部員は先行配信となります。\r\n一定期間後、それぞれ下記の勧誘に追加されます。\r\n\r\n・μ''sのシークレットSSR部員\r\nSSRのイベント報酬部員です。\r\n先行配信した翌月の上旬頃、μ''sの特待生勧誘に追加予定です。\r\n\r\n・AqoursのシークレットSSR部員\r\nSSRのイベント報酬部員です。\r\n先行配信した翌月の中旬頃、Aqoursの特待生勧誘に追加予定です。\r\n\r\nシークレットSSR部員が特待生勧誘に追加された際に、以下の勧誘にも追加されます。\r\n\r\n・補助チケット勧誘\r\nSSR・UR勧誘\r\nUR勧誘\r\n\r\n・その他の勧誘\r\nSR以上確定勧誘\r\nSR以上確定3連勧誘\r\nSSR以上確定勧誘\r\nUR1枚確定11連勧誘\r\n\r\nまた、イベントではUR部員も報酬として登場します。\r\n※イベント報酬として登場するUR部員は、転部・練習によるシール獲得の対象外となります。\r\n\r\n・μ''sのイベント報酬UR部員\r\nURのイベント報酬部員です。\r\n\r\n・Aqoursのイベント報酬UR部員\r\nURのイベント報酬部員です。\r\n\r\n先行配信した翌月に、以下の勧誘にも追加されます。\r\n\r\n・補助チケット勧誘\r\nSR・UR勧誘\r\nSSR・UR勧誘\r\nUR勧誘\r\n\r\n・イベント報酬UR部員の覚醒について\r\nイベント報酬UR部員は、通常の覚醒方法の他、部員ごとに決められた専用の覚醒シールでも覚醒できます。\r\n\r\n専用覚醒シールの名称で、どのイベント報酬UR部員を覚醒させることができるシールかを確認できます。\r\n例：【にこのコーチング】矢澤にこを覚醒させる際は、\r\n[にこのコーチング]専用覚醒シールが使用できます。\r\n\r\n覚醒シールは、イベントの報酬で獲得することができます。\r\n\r\n※SIスキルスロットを解放するルールは通常通りです。','\r',char(13)),'\n',char(10)),replace(replace('活动中作为各类奖励首次登场的神秘SSR为先行公开。\r\n一定期间后将分别加入下列招募。\r\n\r\n・μ’s的神秘SSR社员\r\n这是SSR活动奖励社员。\r\n计划于先行公开的次月上旬加入μ’s优等生招募。\r\n\r\n・Aqours的神秘SSR社员\r\n这是SSR活动奖励社员。\r\n计划于先行公开的次月中旬加入Aqours优等生招募。\r\n\r\n神秘SSR社员加入优等生招募后，下列招募中也将增加。\r\n\r\n・辅助券招募\r\nSSR・UR招募\r\nUR招募\r\n\r\n・其他招募\r\nSR以上确定招募\r\nSSR以上确定招募\r\nUR1张确定11连招募\r\n\r\n此外，UR社员在活动中也会作为奖励登场。\r\n※作为活动奖励登场的UR社员无法通过退社，练习获得贴纸。\r\n\r\n・μ’s的活动奖励UR社员\r\n这是UR活动奖励社员。\r\n\r\n・Aqours的活动奖励UR社员\r\n这是UR活动奖励社员。\r\n\r\n先行公开的次月还将加入下列招募。\r\n\r\n・辅助券招募\r\nSR・UR招募\r\nSSR・UR招募\r\nUR招募\r\n\r\n・关于活动奖励UR社员的觉醒\r\n活动奖励UR社员除了常规的觉醒方法，还可通过各社员特定的专用觉醒贴纸来觉醒。\r\n\r\n可以通过专用觉醒贴纸的名称查看该贴纸能用来觉醒哪名活动奖励UR社员。\r\n例：【微笑指导】矢泽日香觉醒时，\r\n可以使用[微笑指导]专用觉醒贴纸。\r\n\r\n觉醒贴纸可在活动奖励中获得。\r\n\r\n※SI技能槽解锁规则与平时相同。','\r',char(13)),'\n',char(10)),NULL,NULL,NULL);
INSERT INTO help_m VALUES(160,9,12,'スタンプ','表情',replace(replace('スタンプとはつげんの設定・確認ができます。\r\n\r\nスタンプリストとはつげんリストをカスタマイズし、マイセットに登録することができます。\r\nマイセットは5つまで登録可能です。\r\nマイセットをメインに設定することで、イベント中に表示させることができます。\r\n\r\nイベントトップからカスタマイズ画面に移動することも可能です。\r\n\r\nスタンプとはつげんは、SCORE MATCH・なかよしマッチなどのイベントで使用することができます。\r\n\r\nμ''sメンバーやAqoursメンバーのお誕生日当日のログインボーナスや、各種キャンペーンなどでスタンプやはつげんを新規に獲得できる場合があります。','\r',char(13)),'\n',char(10)),replace(replace('可以对表情和发言进行设置与确认。\r\n\r\n玩家可以自行定制表情列表和发言列表，并登录到个人收藏中。\r\n个人收藏最多可以登录五个。\r\n活动中将显示设置为“首选”的个人收藏。\r\n\r\n玩家可以从活动首页跳转到定制界面。\r\n\r\nSCORE MATCH、友情大合战等活动均可使用表情与发言。\r\n\r\nμ''s社员和Aqours社员生日当天的登录奖励、各类宣传活动等都可能获得新的表情与发言。','\r',char(13)),'\n',char(10)),NULL,NULL,NULL);
INSERT INTO help_m VALUES(161,5,1,'登場する部員と出現率','出场社员和出现率',replace(replace('各勧誘ごとに勧誘できる部員と提供割合が設定されています。\r\n\r\n勧誘開催中にラインナップが追加・変更された場合は総部員数の変化により、1部員あたりの出現率は前回登場時と異なりますのでご注意ください。\r\n\r\n勧誘できる部員と提供割合の詳細は、勧誘画面左下の「詳細」をタップしてご確認ください。\r\n\r\n勧誘に登場した部員は、予告なく他の勧誘に再登場する可能性がございます。','\r',char(13)),'\n',char(10)),replace(replace('不同的招募均设置了不一样的可招募社员和提供比例。\r\n\r\n如招募期间阵容出现追加或变更，则与上次出场时相比，单个社员的出现率会因总人数的变化而改变，还望注意。\r\n\r\n点击招募界面左下角的“详细”，即可查看可招募社员和提供比例的详细情况。\r\n\r\n可招募社员可能会未经预告，重新出现在其他招募中。','\r',char(13)),'\n',char(10)),NULL,NULL,NULL);
INSERT INTO help_m VALUES(162,5,4,'BOX勧誘','BOX招募',replace(replace('各レアリティごとに決まった枚数が、BOXに入っている勧誘のことです。\r\nBOX勧誘には、メンバーが選べるタイプのものもあります。\r\nメンバーの選択は、勧誘する前であれば選び直しができます。\r\n1回でも勧誘すると選び直しはできません。\r\n\r\n勧誘する度にBOXの中身は減っていきます。\r\n勧誘に必要なアイテムは、勧誘を行う時に確認できます。\r\n\r\n【抽選に関して】\r\n・抽選は、まず「レアリティ別提供割合」にもとづきレアリティが抽選されます。その後、当選したレアリティに対応する部員が抽選されます。\r\n・各BOX内の残り枚数の変動に応じてレアリティ別提供割合は変動します。\r\n・各レアリティの部員一覧及びレアリティ内における1部員あたりの出現率は勧誘画面左下の「詳細」をタップしてご確認ください。\r\n\r\n【リセット】\r\n・リセット可能なBOX勧誘は任意のタイミングでリセットできます。\r\n・リセット回数が残っている場合は、BOX内の部員をすべて獲得するとBOXは自動的にリセットされます。\r\n・リセットを行うことで、各種確率、残り枚数は初期状態に戻ります。\r\n・リセット回数に制限があったり、リセットができないBOX勧誘も存在します。','\r',char(13)),'\n',char(10)),replace(replace('将各稀有度卡牌以固定的数量加入BOX进行招募。\r\n部分BOX招募可以选择成员。\r\n在开始招募前，可以重新选择成员。\r\n一旦招募过一次，将无法重新选择。\r\n\r\nBOX内的卡牌会随着招募而不断减少。\r\n招募所需道具可以在进行招募时确认。\r\n\r\n【关于抽选】\r\n·首先会根据“各稀有度提供比例”，对稀有度进行抽选。随后才是根据选中的稀有度，抽选相应的社员。\r\n·各稀有度提供比例会随着各BOX内剩余卡牌数量的变化而改变。\r\n·点击招募界面左下角的“详细”，可以查看各稀有度社员列表及单个社员在稀有度内的出现率。\r\n\r\n【重置】\r\n·允许重置的BOX招募可以在任何时候进行重置。\r\n·当重置次数还有剩余时，会在获得BOX内所有社员后自动重置BOX。\r\n·重置后，各种概率和剩余卡牌数量都会回到初始状态。\r\n·部分BOX招募的重置次数设有限制或无法重置。','\r',char(13)),'\n',char(10)),NULL,NULL,NULL);
INSERT INTO help_m VALUES(163,5,6,'チケット','券',replace(replace('勧誘は、ラブカストーンの他に各種チケットを使用して行うこともできます。\r\n\r\n・11連勧誘チケット\r\n1枚で一部の勧誘の11連勧誘をご利用頂けるチケットです。\r\nチケットを所持している場合に利用可能な勧誘で本チケットが選択できます。\r\n\r\n・11連特待生勧誘チケット〜μ''s〜\r\n1枚でμ''sの特待生勧誘の11連勧誘をご利用頂けるチケットです。\r\nチケットを所持している場合に利用可能な勧誘で本チケットが選択できます。\r\n\r\n・11連特待生勧誘チケット〜Aqours〜\r\n1枚でAqoursの特待生勧誘の11連勧誘をご利用頂けるチケットです。\r\nチケットを所持している場合に利用可能な勧誘で本チケットが選択できます。\r\n\r\n・11連特待生勧誘チケット〜Liella!〜\r\n1枚でLiella!の特待生勧誘の11連勧誘をご利用頂けるチケットです。\r\nチケットを所持している場合に利用可能な勧誘で本チケットが選択できます。\r\n\r\n・11連BOX勧誘チケット〜μ''s〜\r\n1枚でμ''sのBOX勧誘の11連勧誘をご利用頂けるチケットです。\r\nチケットを所持している場合に利用可能な勧誘で本チケットが選択できます。\r\n\r\n・11連BOX勧誘チケット〜Aqours〜\r\n1枚でAqoursのBOX勧誘の11連勧誘をご利用頂けるチケットです。\r\nチケットを所持している場合に利用可能な勧誘で本チケットが選択できます。\r\n\r\n・11連BOX勧誘チケット〜ニジガク〜\r\n1枚で虹ヶ咲学園スクールアイドル同好会のBOX勧誘の11連勧誘をご利用頂けるチケットです。\r\nチケットを所持している場合に利用可能な勧誘で本チケットが選択できます。\r\n\r\n・11連BOX勧誘チケット〜Liella!〜\r\n1枚でLiella!のBOX勧誘の11連勧誘をご利用頂けるチケットです。\r\nチケットを所持している場合に利用可能な勧誘で本チケットが選択できます。\r\n\r\n・3連勧誘チケット\r\n1枚で一部の勧誘の3連勧誘をご利用頂けるチケットです。\r\nチケットを所持している場合に利用可能な勧誘で本チケットが選択できます。\r\n※3連勧誘ではSR以上1枚以上確定とはなりません。\r\n\r\n・SR以上確定勧誘チケット〜μ''s〜\r\nμ''sのSR以上の部員のみが登場する『SR以上確定勧誘〜μ''s〜』でご利用頂けるチケットです。\r\nSR以上確定勧誘チケット〜μ''s〜1枚につき1回勧誘できます。\r\n\r\n・SR以上確定勧誘チケット〜Aqours〜\r\nAqoursのSR以上の部員のみが登場する『SR以上確定勧誘〜Aqours〜』でご利用頂けるチケットです。\r\nSR以上確定勧誘チケット〜Aqours〜1枚につき1回勧誘できます。\r\n\r\n・SSR以上確定勧誘チケット〜μ''s〜\r\nμ''sのSSR以上の部員のみが登場する『SSR以上確定勧誘〜μ''s〜』でご利用頂けるチケットです。\r\nSSR以上確定勧誘チケット〜μ''s〜1枚につき1回勧誘できます。\r\n\r\n・SSR以上確定勧誘チケット〜Aqours〜\r\nAqoursのSSR以上の部員のみが登場する『SSR以上確定勧誘〜Aqours〜』でご利用頂けるチケットです。\r\nSSR以上確定勧誘チケット〜Aqours〜1枚につき1回勧誘できます。\r\n\r\n・UR1枚確定11連勧誘チケット〜μ''s〜\r\nμ''sの部員のみが登場する『UR1枚確定11連勧誘〜μ''s〜』でご利用頂けるチケットです。\r\nUR1枚確定11連勧誘チケット〜μ''s〜1枚につき1回11連勧誘ができます。\r\n\r\n・UR1枚確定11連勧誘チケット〜Aqours〜\r\nAqoursの部員のみが登場する『UR1枚確定11連勧誘〜Aqours〜』でご利用頂けるチケットです。\r\nUR1枚確定11連勧誘チケット〜Aqours〜1枚につき1回11連勧誘ができます。\r\n\r\n・SR以上特技サポート勧誘チケット\r\nSR以上の特技アップサポートメンバーのみが登場する『特技アップサポートメンバー勧誘〜μ''s〜』および『特技アップサポートメンバー勧誘〜Aqours〜』でご利用頂けるチケットです。\r\nSR以上特技サポート勧誘チケット1枚につき1回勧誘できます。\r\n※本勧誘は補助チケットでご利用頂ける「特技アップサポートメンバー勧誘」とは別の勧誘です。\r\n\r\nその他の勧誘チケットについては<a href="/webview.php/invitationTicket/index">こちら</a>からご確認いただけます。\r\n\r\n上記に記載している他にも、キャンペーンで販売する特別なセット商品でのみ獲得できるチケットもあります。\r\n詳細はセット商品の販売期間中にお知らせからご確認頂けます。','\r',char(13)),'\n',char(10)),replace(replace('除爱心外，还可以使用各种招募券进行招募。\r\n\r\n·11连招募券\r\n使用1张就能在部分招募进行11连招募的券。\r\n持有招募券时，可以在支持使用的招募中选择本券。\r\n\r\n·3连招募券\r\n使用1张就能在部分招募进行3连招募的券。\r\n持有招募券时，可以在支持使用的招募中选择本券。\r\n※3连招募不会必出1张以上的SR或以上稀有度卡牌。\r\n\r\n·SR或以上招募券〜μ''s〜\r\n可以在只有μ''s的SR或以上社员出场的“必得SR或以上招募〜μ''s〜”中使用的招募券。\r\n1张“SR或以上招募券〜μ''s〜”可以进行1次招募。\r\n\r\n·SR或以上招募券〜Aqours〜\r\n可以在只有Aqours的SR或以上社员出场的“必得SR或以上招募〜Aqours〜”中使用的招募券。\r\n1张“SR或以上招募券〜Aqours〜”可以进行1次招募。\r\n\r\n·SSR或以上招募券〜μ''s〜\r\n可以在只有μ''s的SSR或以上社员出场的“必得SSR或以上招募〜μ''s〜”中使用的招募券。\r\n1张“SSR或以上招募券〜μ''s〜”可以进行1次招募。\r\n\r\n·SSR或以上招募券〜Aqours〜\r\n可以在只有Aqours的SSR或以上社员出场的“必得SSR或以上招募〜Aqours〜”中使用的招募券。\r\n1张“SSR或以上招募券〜Aqours〜”可以进行1次招募。\r\n\r\n·UR1张确定11连招募券〜μ''s〜\r\n可以在只有μ''s社员出场的“UR1张确定11连招募券〜μ''s〜”中使用的招募券。\r\n1张“UR1张确定11连招募券〜μ''s〜”可以进行1次11连招募。\r\n\r\n·UR1张确定11连招募券〜Aqours〜\r\n可以在只有Aqours社员出场的“UR1张确定11连招募券〜Aqours〜”中使用的招募券。\r\n1张“UR1张确定11连招募券〜Aqours〜”可以进行1次11连招募。\r\n\r\n·SR以上技能提升招募券\r\n可以在只有SR或以上的提升技能支援成员出场的“提升技能支援成员招募〜μ''s〜”及“提升技能支援成员招募〜Aqours〜”使用的招募券。\r\n1张“SR以上技能提升招募券”可以进行1次招募。\r\n※本招募有别于可以使用辅助券的“技能提升支援成员招募”。\r\n\r\n\r\n除上述各类招募券外，还有只在特殊活动出现的礼包，包含了特殊招募券。\r\n详细情况可以查阅礼包出售期间的公告。','\r',char(13)),'\n',char(10)),NULL,NULL,NULL);
INSERT INTO help_m VALUES(164,5,7,'各種チケット勧誘','各类券招募',replace(replace('特別なチケットを使ってできる勧誘のことです。\r\n\r\n所持しているチケットは、「アイテムリスト＞その他」から確認できます。\r\n\r\nチケットを所持している場合のみ、勧誘メニューに該当の勧誘が表示されます。\r\n\r\n※各種チケット勧誘は特待生ボーナスの対象外です。','\r',char(13)),'\n',char(10)),replace(replace('可以使用特殊招募券进行的招募。\r\n\r\n当前持有的招募券可以前往“道具一览＞其他”进行查看。\r\n\r\n仅在持有招募券时，招募菜单才会显示相应的招募。\r\n\r\n※各类券招募不享受优等生奖励。','\r',char(13)),'\n',char(10)),NULL,NULL,NULL);
INSERT INTO help_m VALUES(165,9,7,'おさんぽラリー','散步拉力赛',replace(replace('メンバーと一緒にマップを回りながらストーリーが楽しめるイベントです。\r\n\r\n【遊び方】\r\n①イベントptと『おみやげ』を集めよう！\r\nマップ内の『ストーリースポット』ではイベントストーリーやライブが楽しめます。\r\nストーリークリア後、ライブをプレイできるようになります。\r\n『ストーリースポット』のライブでイベントptや『おみやげ』を集めましょう！\r\n『おみやげ』は全部で4種類あり、必要数集めると好きな報酬と交換することができます。\r\n※詳しくは【おみやげについて】をご覧ください。\r\n\r\n②『ストーリースポット』を進めよう！\r\n各『ストーリースポット』は1つ前のストーリースポットで発生したミッションを全てクリアし、イベントptをためることで解放することができます。\r\n詳しい解放条件はマップ内のストーリースポットを長押しすると確認できます。\r\n※スポットロック時はタップでも表示されます。\r\n\r\n③『チャレンジスポット』のライブに挑戦しよう！\r\nストーリースポットをクリアしていくと順に解放される各属性の『チャレンジスポット』では『イベントSCOREランキング』に挑戦することができます。\r\n※『チャレンジスポット』以外のライブはSCOREランキングの対象外です。\r\n※『チャレンジスポット』でプレイできる楽曲はイベントごとに決まった課題曲の中からランダムで選曲されます(属性はスポットごとに固定となります)。詳しくは【対象楽曲について】をご覧ください。\r\n※『チャレンジスポット』でもイベントptやおみやげを獲得することができます。\r\n\r\n\r\n※イベント終了時刻を過ぎてからライブが終了するとイベントptは獲得できません。また、ライブ終了時にメンテナンスに入りますとEXP、G、部員なども獲得できなくなります。終了直前のライブはご注意くださいますようお願いいたします。\r\n\r\n\r\n【報酬獲得条件】\r\n・達成報酬\r\nイベントptの累計獲得数に応じて順次配布\r\n\r\n・累計ptランキング報酬\r\n期間中の累計イベントpt獲得ランキングの順位に応じて配布\r\n\r\n・SCOREランキング報酬\r\n期間中にイベントでプレイした全ての楽曲を対象とした「SCOREランキング」の順位に応じて配布\r\n\r\nSCOREランキングページでは、上位10人のハイスコアを獲得した時のユニットや楽曲を閲覧することもできます。\r\nまた、友達ランキングページでも、友達のユニットや楽曲を閲覧できます。\r\n参考にして上位を目指しましょう！\r\n\r\n※ランキング報酬を獲得できる人数は他イベントと同様で、変更はございません。\r\n※ランキング報酬の配布はイベント終了後となります。\r\n\r\n・おみやげ交換所\r\n各スポットで集めた『おみやげ』を交換することで獲得\r\n※特定の条件を達成することで交換可能になる報酬もあります。\r\n※おみやげ交換報酬は報酬によって必要なおみやげの種類と数が異なります。\r\n\r\n\r\n【対象楽曲について】\r\nおさんぽラリーの『チャレンジクエスト』対象楽曲については以下の通りです。\r\nスマイルスポット\r\n・EASY/NORMAL/HARD/EXPERT：各6曲がランダムで出現\r\n\r\nピュアスポット\r\n・EASY/NORMAL/HARD/EXPERT：各6曲がランダムで出現\r\n\r\nクールスポット\r\n・EASY/NORMAL/HARD/EXPERT：各6曲がランダムで出現\r\n\r\n\r\n【おみやげについて】\r\n『おみやげ』は全部で4種類あり、『おさんぽラリー』の各種スポットでライブをクリアすると獲得できます。\r\nスポットごとに獲得可能な『おみやげ』の種類と量は異なり、またプレイごとに変動します。\r\n※通常ライブでは『おみやげ』を獲得することはできません。ご注意下さい。\r\n\r\n『おみやげ』は『おみやげ交換所』で好きな報酬と交換することができます。\r\n『おみやげ交換所』で交換できる報酬は一部を除き、種類ごとに交換可能回数が決まっています。\r\n\r\nイベント期間終了後は『おみやげ』を獲得することができませんが、結果発表期間中に『おみやげ』を交換することは可能です。\r\n結果発表期間終了時点で未交換の『おみやげ』は自動的に種類問わず1個あたり50Gに変換されます。\r\n※プレゼントボックスから受け取れます。\r\n\r\n\r\n※『おさんぽラリー』の各種スポットでの楽曲クリア数もライブ課題の達成数にカウントされます。\r\n※『おさんぽラリー』のライブも『ファーストライブボーナス』の受け取り対象となります。\r\n※『おさんぽラリー』のライブでもごほうびBOX及びスペシャルごほうびBOXのゲージは加算されます。『チャレンジスポット』のライブではゲージの増加にボーナスが加算されます。\r\n※クリア済スポットのストーリーについては、イベント終了後、結果発表期間が終了するまでマップから閲覧することができます。結果発表期間の終了後に各モードの『その他のストーリー』に追加されます。\r\n※イベントに参加しなかった場合でも、結果発表期間に1話までのストーリーが閲覧できます。結果発表期間中にイベントバナーからマップを開いた場合、結果発表期間の終了後に1話までのストーリーが各モードの『その他のストーリー』に追加されます。一度もマップを開かなかった場合は追加されません。\r\n※イベント終了後は条件を満たしていた場合でもスポットの解放やクリアはできません。\r\n※上記期間中に獲得できなかったストーリーは、翌月末のアップデート後に「イベントストーリー解放キー」を使用することで獲得できるようになります。\r\n','\r',char(13)),'\n',char(10)),replace(replace('本活动为一边与社员一起巡游地图，一边观看剧情。\r\n\r\n【游玩方法】\r\n①收集活动积分和“纪念品”！\r\n地图上的“剧情格子”可以观看活动剧情并游玩演唱会。\r\n通关剧情后，可以游玩演唱会。\r\n通过“剧情格子”的演唱会收集活动积分和“纪念品”吧！\r\n“纪念品”一共有四种，集齐必要数量就能自行兑换奖励。\r\n※详细内容请查阅【关于纪念品】。\r\n\r\n②推进“剧情格子”吧！\r\n完成前一个剧情格子出现的所有任务并积攒活动积分，就能解锁下一个“剧情格子”。\r\n长按地图上的剧情格子，就能查看详细解锁条件。\r\n※点击上锁的格子也能显示解锁条件。\r\n\r\n③向“挑战格子”的演唱会发起冲击！\r\n在“活动积分排行榜”，可以游玩随剧情格子的通关而解锁的各属性“挑战格子”。\r\n※“挑战格子”以外的演唱会不会被列入分数排行榜。\r\n※可以在“挑战格子”中游玩的乐曲将从各活动选定的课题曲目中随机抽选（各格子固定属性）。详细内容请查阅【关于对象乐曲】\r\n※“挑战格子”也能获得活动积分和纪念品。\r\n\r\n\r\n※如结束演唱会时已经超过活动结束时间，将无法获得活动积分。此外，如结束演唱会时若处于维护期间，将无法获得经验值、金币和社员等。在临近活动结束期间游玩演唱会时，请多多注意。\r\n\r\n\r\n【奖励获得条件】\r\n·完成奖励\r\n根据活动积分的累计获得数量依次发放\r\n\r\n·累计积分排行榜奖励\r\n根据活动期间累计获得活动积分排行榜的名次依次发放\r\n\r\n·分数排行榜奖励\r\n根据活动期间游玩的所有乐曲“分数排行榜”的名次依次发放\r\n\r\n分数排行榜可以浏览前十名获得高分时的所用角色与乐曲。\r\n还可以在好友排行榜页面浏览好友的所用角色和乐曲。\r\n以此为参考，力争上游吧！\r\n\r\n※可以获得排行榜奖励的人数与其他活动一样，不作变更。\r\n※排名奖励将在活动结束后发放。\r\n\r\n·纪念品兑换所\r\n兑换各格子收集的“纪念品”获得\r\n※部分奖励需要完成特定条件才能兑换。\r\n※在纪念品兑换奖励中，不同奖励所需纪念品的种类与数量各不相同。\r\n\r\n\r\n【关于对象乐曲】\r\n散步拉力赛的“挑战任务”对象乐曲如下所示。\r\n甜美格子\r\n·EASY/NORMAL/HARD/EXPERT：各6首歌随机出现\r\n\r\n清纯格子\r\n·EASY/NORMAL/HARD/EXPERT：各6首歌随机出现\r\n\r\n洒脱格子\r\n·EASY/NORMAL/HARD/EXPERT：各6首歌随机出现\r\n\r\n\r\n【关于纪念品】\r\n“纪念品”一共四种，完成“散步拉力赛”各格子的演唱会后即可获得。\r\n“纪念品”的种类与数量将随着格子的位置而改变，每次游玩时也会发生变化。\r\n※常规演唱会无法获得“纪念品”，请多多注意。\r\n\r\n“纪念品”可以在“纪念品兑换所”自行兑换奖励。\r\n除部分奖励外，“纪念品兑换所”可以兑换的每种奖励都设有次数限制。\r\n\r\n活动结束后无法继续获得“纪念品”，但可以在公布结果期间兑换“纪念品”。\r\n结果公布期间结束后，所有种类的未兑换“纪念品”都将自动变更为每个50金币。\r\n※需要前往礼物箱领取。\r\n\r\n\r\n※“散步拉力赛”中各格子的乐曲通关数量计入课题中的完成数量。\r\n※“散步拉力赛”的演唱会属于“首场演唱会奖励”领取对象。\r\n※“散步拉力赛”的演唱会计入奖励箱和特殊奖励箱槽。“挑战格子”演唱会的奖励箱槽增加量将获得加成。\r\n※活动结束后，可以在结果公布期间从地图上观看已通关格子的剧情。结果公布期间结束后，这些剧情将被追加到各模式的“其他剧情”。\r\n※即使没有参加活动，也可以在结果公布期间观看第1话以前的剧情。只要在结果公布期间通过活动横幅打开地图，第1话以前的剧情就将在结果公布期间结束后被追加到各模式的“其他剧情”。如果从未打开地图，将不会追加剧情。\r\n※活动结束后，即使满足条件，也无法解锁或通关格子。\r\n※可以在下月底版本升级后使用“活动剧情解锁钥匙”解锁未能在上述时间内获得的剧情。','\r',char(13)),'\n',char(10)),NULL,NULL,NULL);
INSERT INTO help_m VALUES(166,9,8,'ライブ♪アリーナ','演唱会♪竞技场',replace(replace('大勢のスクールアイドルとセッションし、目標SCOREの達成を目指すイベントです。\r\n※ライブ♪アリーナでは『イベントアイコン』は登場しません。\r\n\r\n【スペシャルセッション】\r\n「テンションゲージ」が最大になると挑戦できるセッションです。\r\n目標SCOREを達成するとセッション成功です。\r\nセッションを成功させて、さまざまな報酬の獲得を目指しましょう！\r\n※スペシャルセッションのスキップはできません。\r\n\r\n【ライブセッション】\r\nライバルたちと挑戦するセッションです。\r\nライブクリア時にセッション成功・失敗に応じ「テンションゲージ」が上昇します。\r\nまた、セッション成功・失敗に応じ「アイテム」が獲得できます。\r\n獲得した「アイテム」は「交換所」で報酬アイテムと交換できます。\r\n※ライブ失敗時にはテンションゲージとアイテムの獲得はできません。\r\n\r\n＜テンションゲージ＞\r\nライブをクリアすると上昇します。\r\n「ライブセッション」に成功すると最も大きく上昇します。\r\nライブ♪アリーナ以外でのライブをクリアしても上昇します。\r\n※体力がなくなりライブ失敗となった場合や、プライベートセッションではテンションゲージを獲得できません。\r\n\r\n【ドリームセッション】\r\n特別な部員と挑戦するセッションです。\r\n「スペシャルセッション」のクラスがエキスパート以上のクラスでライブセッションをすると、特別な部員が登場することがあります。\r\n登場する部員はユニットのセンター部員によって変化します。\r\n制限時間内にライブをプレイし、目標SCOREを達成してセッション成功すると特別な報酬を獲得できます。\r\n\r\n【プライベートセッション】\r\n「うでまえID」を入力し、指定の友達やライバルと挑戦するセッションです。\r\n「うでまえID」は「うでまえチェック」の画面右上部に表示される「ID発行」ボタンをタップすると発行されます。\r\n「テンションゲージ」の上昇や、報酬はありません。\r\n\r\n【スペシャルセッション報酬】\r\nスペシャルセッションの報酬は登場する部員とのセッションに初めて成功したときに獲得ができます。\r\n合計50回のセッションに初成功をするとライブ♪アリーナ専用の称号が獲得できます。\r\n\r\nマンスリーランキング報酬はライブ♪アリーナに遷移せずに受け取りが可能です。\r\n\r\n【アシスト部員】\r\n編成すると「ライブ♪アリーナ」イベント内で下記どちらかの効果があらわれる部員です。\r\n\r\n＜アシスト効果＞\r\n・効果1：確率で獲得できる報酬の獲得率が効果値分アップします。\r\n・効果2：確率で獲得できる報酬が1種類増えます。\r\n※効果1は効果2で増えた報酬の獲得率には影響しません。\r\n※アシスト効果は、プライベートセッション以外のセッションであらわれます。','\r',char(13)),'\n',char(10)),replace(replace('这是与众多学园偶像同台公演，达成目标得分的活动。\r\n※在演唱会♪竞技场中没有“活动图标”登场。\r\n\r\n【特殊竞演】\r\n“热情值槽”蓄满后可以挑战该竞演。\r\n达成目标得分后视为竞演成功。\r\n让竞演大获成功，获得各式各样的奖励吧！\r\n※特别竞演无法跳过。\r\n\r\n【演唱会竞演】\r\n这是和竞争对手挑战的竞演。\r\n完成演唱会后，“热情值槽”将根据竞演的成功・失败上升。\r\n此外，根据竞演的成功・失败还可以获得“道具”。\r\n获得的“道具”可以在“兑换所”兑换奖励道具。\r\n※演唱会失败时无法获得热情和道具。\r\n\r\n＜热情值槽＞\r\n完成演唱会时上升。\r\n“演唱会竞演”成功时的上升量最大。\r\n完成演唱会♪竞技场以外的演唱会时也会上升。\r\n※参加私下竞演或体力耗尽，演唱会失败时，无法获得热情。\r\n\r\n【梦幻竞演】\r\n这是和特殊社员挑战的竞演。\r\n通过专家以上的“特别竞演”级别参加演唱会竞演时，可能会有特殊社员登场。\r\n登场社员视组合C位社员而异。\r\n在限制时间内举办演唱会，达成目标得分后便视为竞演成功，可以获得特别奖励。\r\n\r\n【私人竞演】\r\n这是输入“实力ID”，和指定好友或竞争对手挑战的竞演。\r\n在“实力测试”画面中点击右上角的“生存ID”便可生成“实力ID”。\r\n无法增加“热情值槽”或获得奖励。\r\n\r\n【特别竞演奖励】\r\n首次和登场的社员竞演成功时，可以获得特别竞演奖励。\r\n竞演共计首次成功50次后，可以获得演唱会♪竞技场专用的称号。\r\n\r\n每月排行的奖励无需跳转到演唱会♪竞技场即可领取。\r\n\r\n【辅助社员】\r\n将这里社员编入编队后，“演唱会♪竞技场”活动内将出现下列效果中的任意一项。\r\n\r\n＜辅助效果＞\r\n・效果1：提高以一定概率出现的奖励获得率！\r\n・效果2：将以一定概率出现的奖励增加一种！\r\n※效果1不会影响效果2增加的奖励获得率。\r\n※辅助效果适用于私人竞演以外的竞演。','\r',char(13)),'\n',char(10)),NULL,NULL,NULL);
INSERT INTO help_m VALUES(167,3,22,'アクセサリー','饰品',replace(replace('部員にセットすることで、そのアクセサリーが持つ「特技」をライブで使用できるようになるアイテムです。\r\nアクセサリーの特技は、ライブ中、発動条件を満たした部員の特技が発動しなかった場合に、確率で発動します。\r\nアクセサリーには部員と同様に属性Pがあり、アクセサリーをセットすることで部員の能力が上がります。\r\n各アクセサリーにはLvがあり、Lvに応じて発動する特技の効果と属性Pが変化します。\r\n\r\n共通アクセサリーと専用アクセサリーの効果や対象部員については<a href="/webview.php/accessory/index">こちら</a>からご確認いただけます。\r\n\r\n＜共通アクセサリー＞\r\nサポートメンバーおよび特技の無い部員を除く任意の部員にセットできるアクセサリーです。\r\n部員2人を使って「メイク」することで入手できます。\r\nメイクに使用した部員は消失しますのでご注意ください。\r\n\r\n＜専用アクセサリー＞\r\nセットできる部員が限定されているアクセサリーです。\r\n同一の対象部員2人を使って「メイク」することで、その部員専用のアクセサリーを入手できます。\r\n専用アクセサリーをLvMAXにすると、対象部員と同名の部員（一部部員を除く）にセットできるようになります。\r\nメイクに使用した部員は消失しますのでご注意ください。\r\n\r\n例）\r\nURいちごアイスの手帳(Lv1)：\r\nUR高坂穂乃果[10月編(チア) 前半]にのみセット可能\r\n\r\nURいちごアイスの手帳(LvMAX)：\r\n他の「高坂穂乃果」にもセット可能\r\n（ただし特技のない部員やSSR高坂穂乃果[パワーパフ ガールズコラボ]やR高坂穂乃果[μ''s リトルメモリー編]などの一部の部員にはセットできません）\r\n\r\n＜アクセサリー素材＞\r\n・グラスパーツ\r\n素材として使用することでアクセサリーのデコレートを行うことができます。\r\nグラスパーツにはU、SS、S、R、Nのレアリティがあり、各レアリティごとに獲得できる経験値は異なります。\r\n\r\n・ジュエルパーツ\r\n素材として使用することでアクセサリーのリメイクを行うことができます。\r\nジュエルパーツにはU、SS、S、R、Nのレアリティがあり、リメイク対象のアクセサリーのレアリティによりリメイクに必要な素材数が異なります。','\r',char(13)),'\n',char(10)),replace(replace('饰品是可以让装备的社员在演唱会中使用相应“技能”的道具。\r\n在演唱会中，如果满足发动条件的社员未发动技能，则有一定概率发动饰品的技能。\r\n饰品与社员同样具备属性P，装备饰品可以提高社员的能力。\r\n各饰品存在等级，发动技能的效果和属性P将根据等级波动。\r\n\r\n＜通用饰品＞\r\n这是除辅助成员和没有技能的社员以外，任何社员都可装备的饰品。\r\n可以通过消耗2名社员的“装扮”来获得。\r\n请注意用于装扮的社员将会消失。\r\n\r\n＜专用饰品＞\r\n这是可装备社员受到限制的饰品。\r\n可以通过消耗2名相同目标社员的“装扮”来获得该社员的专用饰品。\r\n专用饰品的等级升满后，可以让与目标社员同名的社员（部分社员例外）装备。\r\n请注意用于装扮的社员将会消失。\r\n\r\n例）\r\nUR草莓冰淇淋笔记本(等级1)：\r\n仅UR高坂穗乃果[10月篇（啦啦队）上]可装备\r\n\r\nUR草莓冰淇淋笔记本(等级MAX)：\r\n其他的“高坂穗乃果”皆可装备\r\n（但是没有技能的社员以及高坂穗乃果[μ’s 小小回忆篇]等部分社员无法装备）','\r',char(13)),'\n',char(10)),NULL,NULL,NULL);
INSERT INTO help_m VALUES(168,5,5,'ピックアップブースト勧誘','Pick up Boost招募',replace(replace('指定ラインナップから選んだUR部員1人の出現率をアップさせることができる勧誘です。\r\n11連勧誘を行うごとに、選択した部員の「ピックアップブーストLv」が上昇し、Lvに応じた出現率になります。\r\n\r\n・レアリティ別の排出率は変わりません。\r\n・「ピックアップブーストLv」には上限があります。\r\n・対象部員ごとに「ピックアップブーストLv」があります。\r\n・ピックアップブーストの対象に選択した部員は何度でも変更が可能です。\r\n・ピックアップブーストの対象に選択した部員を変更した場合、変更後の部員の「ピックアップブーストLv」が適用されます。\r\n・選択を解除した対象部員の上昇した「ピックアップブーストLv」は、対象勧誘の開催期間中、維持されます。\r\n\r\nピックアップブーストLvの上昇による出現率の変動例\r\nUR部員の排出率が1%の勧誘の場合\r\n・ピックアップブーストLv1\r\nUR部員の排出率1%の中で、対象に指定した部員が、50%の確率で出現します。\r\n・ピックアップブーストLvMAX(Lv9)\r\nUR部員の排出率1%の中で、対象に指定した部員が、90%の確率で出現します。','\r',char(13)),'\n',char(10)),replace(replace('可以从指定阵容中选择一名UR社员提高出现率的招募。\r\n每进行一次11连招募，都能提升所选社员的“Pick up Boost Lv”，出现率也会随等级而提升。\r\n\r\n·不同稀有度的出现率没有变化。\r\n·“Pick up Boost Lv”存在上限。\r\n·每个对象社员拥有独立的“Pick up Boost Lv”。\r\n·选作“Pick up Boost”对象的社员可以随意变更。\r\n·变更选作“Pick up Boost”对象的社员时，将适用变更后社员的“Pick up Boost Lv”。\r\n·在召开相关招募期间，被取消选择的社员之前所提升的“Pick up Boost Lv”将被保留。\r\n\r\n出现率随“Pick up Boost Lv”提升而改变的示范\r\n如为UR社员出现率1%的招募\r\n·Pick up Boost Lv1\r\n在UR社员1%的出现率中，出现所选社员的概率为50%。\r\n·Pick up Boost LvMAX（Lv9）\r\n在UR社员1%的出现率中，出现所选社员的概率为90%。','\r',char(13)),'\n',char(10)),NULL,NULL,NULL);
INSERT INTO help_m VALUES(169,3,23,'衣装','服装',replace(replace('同一メンバーの部員のイラストを、衣装として「着せ替え登録」することができます。\r\n「着せ替え登録」を行った衣装は他の部員へ着せ替えることができます。\r\n着せ替えを行った部員は外見のみ変更され、能力やパラメータには影響しません。\r\n\r\n＜クロゼット＞\r\nクロゼットでは、「着せ替え登録」と登録済みの部員を確認できます。\r\n\r\n・「部員」タブ\r\n「着せ替え」用に登録可能な部員が一覧で表示されます。\r\n条件を満たしている部員をタップすると確認のポップアップが表示され、「登録する」をタップすると、衣装としてクロゼットに登録されます。\r\n覚醒前後で外見が異なる部員は、覚醒前後の衣装が同時に登録されます。\r\n\r\n画面上部の「着せ替え登録できる部員のみ表示」のチェックを解除することで、「着せ替え登録」済みの部員や、「着せ替え登録」の条件を満たしていない部員も表示できます。\r\n「着せ替え登録」の条件を満たしていない部員は暗転表示され、タップすると必要な条件を確認することができます。\r\n\r\n■着せ替え登録できる部員の条件\r\nUR部員　覚醒済かつLv120以上\r\nSSR部員　覚醒済かつLv90以上\r\nSR部員　覚醒済かつLv80以上\r\nR部員　覚醒済かつLv60以上\r\nN部員　覚醒済かつLv40以上\r\n\r\n・「クロゼット」タブ\r\n「着せ替え」用に登録済みの部員が一覧で表示されます。\r\n\r\n＜着せ替え＞\r\n・「部員」タブ\r\n着せ替えをしたい部員を選択すると、画面左に「着せ替えする部員」と画面右に「衣装一覧」が表示されます。\r\n\r\n「衣装一覧」から着せ替えたい衣装を選択して、右下にある「着せ替えする」ボタンをタップすると確定します。\r\n\r\n＜着せ替えする部員＞\r\n・「着せ替え変更」ボタン\r\n着せ替え元となる部員を変更することができます。\r\n・「着せ替え解除」ボタン\r\nタップすると着せ替えを解除し、本来の外見表示がされます。右下にある「着せ替えする」ボタンをタップすると確定します。\r\n表示される部員名、Lv、特技Lv、属性Pは着せ替え元の部員のものが表示されます。\r\n\r\n＜衣装一覧＞\r\n・現在着用している衣装には「SET」アイコンが表示されます。\r\n・着せ替え登録が可能な未登録の部員には「着せ替え登録可能」と表示がされます。','\r',char(13)),'\n',char(10)),replace(replace('相同人物的社员插画可以作为服装来“登录换装”。\r\n“登录换装”的服装可供其他社员换装。\r\n进行换装的社员仅外表发生改变，能力和属性值不受影响。\r\n\r\n＜衣柜＞\r\n衣柜可以查看“登录换装”和已登录的社员。\r\n\r\n·“社员”标签\r\n显示可登录“换装”的社员列表。\r\n点击满足条件的社员将显示确认弹窗，随后点击“进行登录”，即可将该服装登录到衣柜里。\r\n对于觉醒前后外观发生变化的社员，将同时登录觉醒前后的服装。\r\n\r\n取消屏幕上方的“仅显示可换装登记的社员”勾选，就能显示已完成“登录换装”的社员和尚未满足“登录换装”条件的社员。\r\n未满足“登录换装”条件的社员将以深色显示，点击后可以查看所需条件。\r\n\r\n■可以登录换装的成员条件\r\nUR社员 已觉醒且Lv120以上\r\nSSR社员 已觉醒且Lv90以上\r\nSR社员 已觉醒且Lv80以上\r\nR社员 已觉醒且Lv60以上\r\nN社员 已觉醒且Lv40以上\r\n\r\n·“衣柜”标签\r\n显示已登录“换装”的社员列表。\r\n\r\n＜换装＞\r\n·“社员”标签\r\n选择想要换装的社员后，屏幕左侧将显示“待换装社员”，右侧将显示“服装列表”。\r\n\r\n从“服装列表”选择想要换上的服装，再点击右下角的“进行换装”键，即可完成换装。\r\n\r\n＜换装的社员＞\r\n·“更改换装”键\r\n可以变更为原本登录换装的社员。\r\n·“解除换装”键\r\n点击后将解除换装，显示原本的外观。点击右下角的“进行换装”键，即可完成换装。\r\n所显示的社员名、等级、技能等级、属性值均为原本登录换装社员的数值。\r\n\r\n＜服装一览＞\r\n·当前穿戴的服装将显示“SET”图标。\r\n·已满足条件但尚未登录的社员将显示“可换装登录”。','\r',char(13)),'\n',char(10)),NULL,NULL,NULL);
INSERT INTO help_m VALUES(170,6,5,'着せ替え衣装','换装服装',replace(replace('着せ替え衣装は、着せ替え登録を行う必要が無く、最初から「着せ替え」が可能な衣装です。\r\n着せ替え衣装を入手すると、クロゼットに直接追加されます。\r\n\r\n着せ替え衣装は1着につき1種類となります。\r\n\r\n※着せ替え衣装は部員とは異なり、ユニット編成、練習、特別練習、特訓、アクセサリー、部員リスト、控え室、部員切り替え、転部の対象として表示されません。','\r',char(13)),'\n',char(10)),replace(replace('换装服装是从一开始就能进行“换装”的服装，无需登录换装。\r\n获得的换装服装将直接加入衣柜。\r\n\r\n每件换装服装只有一种。\r\n\r\n※换装服装不同于社员，不会作为编辑队伍、练习、特训、集中训练、饰品、服装、预备教室、社员切换、退社的对象显示。','\r',char(13)),'\n',char(10)),NULL,NULL,NULL);
INSERT INTO help_m VALUES(172,7,30,'おもいでギャラリー','回忆画廊',replace(replace('おもいでギャラリーとは、スクフェス全国大会で優勝者が選択した公約メモリーズや公約ボイスなどのコンテンツを見ることができる機能です。\r\n\r\n各コンテンツはそれぞれ設定された課題をクリアすることで解放することができます。\r\nさらに、コンテンツを解放すると「メモリアルパワー」という特別なパラメーターが付与され、ユニット全体のステータスを上昇させることができます。\r\n\r\nおもいでギャラリーは、メニュー＞おもいでギャラリーのボタンから確認することができます。','\r',char(13)),'\n',char(10)),replace(replace('回忆画廊是查看学园偶像祭全国大会冠军所选择的誓言回忆、誓言语音等内容的功能。\r\n\r\n完成各项设置的课题，就能解锁内容。\r\n另外，解锁内容还能获得名为“回忆之力”的特殊属性值，用来提升整个小组的状态。\r\n\r\n回忆画廊可以通过“菜单＞回忆画廊”键进行查看。','\r',char(13)),'\n',char(10)),NULL,NULL,NULL);
INSERT INTO help_m VALUES(173,9,13,'セットリスト','歌单',replace(replace('セットリストイベントは、決められたセットリストの楽曲を1曲ずつ順番にプレイするイベントです。\r\nイベントに参加して、最高ランクを目指しましょう！\r\n\r\n■概要\r\n・各楽曲のライブクリア時、ライブのスコアに応じて「エモーションpt」と「ライブスタンプ」を獲得できます。\r\n・「ライブスタンプ」はセットリストの全曲クリア時にも獲得することができます。\r\n・集めた「ライブスタンプ」を消費して、「BOXくじ」をひくことができます。\r\n・各BOXの「注目の報酬」を獲得したあとは、任意のタイミングで次のBOXくじに切り替えることができます。\r\n・BOX内の報酬をすべて獲得すると、次のBOXくじに切り替わります。\r\n\r\n■注意事項\r\n・「ライブスタンプ」はイベント期間中獲得可能です。BOXくじの開催期間終了後は使用できなくなりますのでご注意ください。\r\n・遊び方の詳細につきましてはイベント内の遊び方もあわせてご確認ください。','\r',char(13)),'\n',char(10)),replace(replace('歌单活动指的是逐一游玩规定歌单中所有歌曲的活动。\r\n参加活动并向着最高段位而努力吧！\r\n\r\n■概要\r\n·完成每首歌的演唱会时，可以根据演唱会分数获得“情感点数”和“演唱会印章”。\r\n·完成歌单内所有歌曲时，也有可能获得“演唱会印章”。\r\n·可以使用收集的“演唱会印章”，在“抽奖箱”内进行抽奖。\r\n·获得每个抽奖箱的“关注奖励”后，就能随时切换到下一个抽奖箱。\r\n·获得箱中所有奖励后，将自动切换到下一个抽奖箱。\r\n\r\n■注意事项\r\n·请注意，“演唱会印章”只能在活动期间获得，无法在抽奖箱活动结束后使用。\r\n·详细的游戏方式可以前往活动内游戏方式介绍进行查阅。','\r',char(13)),'\n',char(10)),NULL,NULL,NULL);
